import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { CSVLink } from 'react-csv';
import { useNavigate } from 'react-router-dom';

const TransactionHistory = () => {
  const [transactions, setTransactions] = useState([]);
  const [totals, setTotals] = useState({ sent: 0, received: 0, net: 0 });

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('/api/transactions', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setTransactions(res.data);

        // Summary totals:
        let sent = 0;
        let received = 0;

        res.data.forEach(tx => {
          if (tx.type === 'REPAYMENT') {
            received += tx.amount;
          } else if (tx.type === 'DISBURSEMENT') {
            sent += tx.amount;
          }
        });

        setTotals({ sent, received, net: received - sent });
      } catch (err) {
        console.error('Failed to fetch transactions:', err);
      }
    };

    fetchTransactions();
  }, []);

  const headers = [
    { label: 'Date', key: 'timestamp' },
    { label: 'Type', key: 'type' },
    { label: 'Amount', key: 'amount' },
    { label: 'From', key: 'fromUser.name' },
    { label: 'To', key: 'toUser.name' },
  ];

  return (
    <div>
      <h2>Transaction History</h2>

      <div>
        <p><strong>Total Sent:</strong> ${totals.sent.toFixed(2)}</p>
        <p><strong>Total Received:</strong> ${totals.received.toFixed(2)}</p>
        <p><strong>Net:</strong> ${totals.net.toFixed(2)}</p>
      </div>

      <CSVLink data={transactions} headers={headers} filename="transactions.csv">
        <button>⬇️ Download CSV</button>
      </CSVLink>

      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>From</th>
            <th>To</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(tx => (
            <tr key={tx.id}>
              <td>{new Date(tx.timestamp).toLocaleString()}</td>
              <td>{tx.type}</td>
              <td>${tx.amount.toFixed(2)}</td>
              <td>{tx.fromUser?.name}</td>
              <td>{tx.toUser?.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TransactionHistory;
