// src/features/money/MoneySummary.jsx
import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './MoneySummary.css';
import UserProfileModal from '../../components/UserProfileModal';
import LoanThreadModal from '../loans/LoanThreadModal';

// helper: decode JWT for current user id
function getCurrentUserId() {
  const t = localStorage.getItem('token');
  if (!t) return null;
  try {
    const payload = JSON.parse(atob(t.split('.')[1] || ''));
    return payload?.userId ?? null;
  } catch {
    return null;
  }
}

const fmtMoney = (n) =>
  typeof n === 'number' && !Number.isNaN(n) ? `$${n.toFixed(2)}` : 'â€”';

// one-line row layout used across all sections
const ROW_GRID = {
  display: 'grid',
  gridTemplateColumns: 'minmax(160px,1fr) 140px 180px auto auto',
  alignItems: 'center',
  gap: 16,
};

const MoneySummary = () => {
  const [summary, setSummary] = useState({
    loansGiven: [],
    loansReceived: [],
    myOpenRequests: [],
  });
  const [loading, setLoading] = useState(true);

  // expanded panels (per card)
  const [expandedLoans, setExpandedLoans] = useState({});
  // section accordions
  const [openSec, setOpenSec] = useState({
    open: true,
    given: true,
    received: true,
  });

  const [payLoading, setPayLoading] = useState({});
  const [profileUserId, setProfileUserId] = useState(null);

  // discussion modal
  const [threadLoanId, setThreadLoanId] = useState(null);

  const token = localStorage.getItem('token');
  const navigate = useNavigate();
  const authHeader = token ? { Authorization: `Bearer ${token}` } : {};

  /** ------------------ Fetch helpers (market + direct) ------------------ */

  const fetchMarketOpenRequests = async () => {
    const me = getCurrentUserId();
    if (!me) return [];

    // Preferred: /api/loan-requests/mine?status=OPEN
    try {
      const r = await fetch('/api/loan-requests/mine?status=OPEN', { headers: authHeader });
      if (r.ok) {
        const list = await r.json();
        const items = Array.isArray(list) ? list : Array.isArray(list?.items) ? list.items : [];
        return items
          .filter((it) => (it?.borrowerId || it?.borrower?.id) === me)
          .map((r) => ({
            type: 'market',
            id: r.id,
            amount: Number(r.amount),
            duration: Number(r.duration ?? r.months),
            interestRate: Number(r.interestRate ?? r.apr),
            purpose: r.purpose || '',
            createdAt: r.createdAt || null,
            borrowerName: r?.borrower?.name || 'You',
            borrowerId: r?.borrower?.id || r.borrowerId || me,
          }));
      }
    } catch {}

    // Fallback: /api/loans/open then filter to mine
    try {
      const resOpen = await fetch('/api/loans/open', { headers: authHeader });
      if (resOpen.ok) {
        const openListRaw = await resOpen.json();
        const openList = Array.isArray(openListRaw)
          ? openListRaw
          : Array.isArray(openListRaw?.items)
          ? openListRaw.items
          : [];
        const mine = openList.filter((r) => {
          const borrowerId = r?.borrower?.id || r?.borrowerId;
          return borrowerId && borrowerId === me;
        });
        return mine.map((r) => ({
          type: 'market',
          id: r.id,
          amount: Number(r.amount),
          duration: Number(r.duration ?? r.months),
          interestRate: Number(r.interestRate ?? r.apr),
          purpose: r.purpose || '',
          createdAt: r.createdAt || null,
          borrowerName: r?.borrower?.name || 'You',
          borrowerId: r?.borrower?.id || r?.borrowerId || me,
        }));
      }
    } catch {}

    return [];
  };

  const fetchDirectOpenRequests = async () => {
    const me = getCurrentUserId();
    if (!me) return [];

    try {
      const r = await fetch('/api/direct-requests/mine', { headers: authHeader });
      if (!r.ok) return [];
      const data = await r.json();

      // Accept either a flat array or {asBorrower, asLender}
      const combined = Array.isArray(data)
        ? data
        : [
            ...(Array.isArray(data?.asBorrower) ? data.asBorrower : []),
            ...(Array.isArray(data?.asLender) ? data.asLender : []),
          ];

      const FINAL = new Set(['DECLINED', 'CANCELLED', 'DISBURSED', 'APPROVED', 'REJECTED']);
      return combined
        .filter((d) => !FINAL.has((d?.status || '').toUpperCase()))
        .map((d) => {
          const borrowerId = d.borrowerId || d?.borrower?.id;
          const lenderId = d.lenderId || d?.lender?.id;
          const iAmBorrower = borrowerId === me;

          return {
            type: 'direct',
            id: d.id,
            amount: Number(d.amount),
            duration: Number(d.months ?? d.duration),
            interestRate: Number(d.apr ?? d.interestRate),
            createdAt: d.createdAt || null,
            borrowerId,
            borrowerName: d?.borrower?.name || (iAmBorrower ? 'You' : ''),
            lenderId,
            lenderName: d?.lender?.name || (!iAmBorrower ? 'Counterparty' : ''),
            status: d.status || 'PENDING',
          };
        });
    } catch {
      return [];
    }
  };

  const fetchSummary = useCallback(async () => {
    if (!token) return;
    setLoading(true);
    try {
      // main summary (loans given/received)
      const res = await fetch('/api/users/my-money-summary', { headers: authHeader });
      let loansGiven = [];
      let loansReceived = [];
      if (res.ok) {
        const data = await res.json();
        loansGiven = Array.isArray(data.loansGiven)
          ? data.loansGiven
          : Array.isArray(data.loansAsLender)
          ? data.loansAsLender
          : [];
        loansReceived = Array.isArray(data.loansReceived)
          ? data.loansReceived
          : Array.isArray(data.loansAsBorrower)
          ? data.loansAsBorrower
          : [];
      }

      // open requests: market + direct
      const [marketOpen, directOpen] = await Promise.all([
        fetchMarketOpenRequests(),
        fetchDirectOpenRequests(),
      ]);
      const myOpenRequests = [...marketOpen, ...directOpen];

      setSummary({ loansGiven, loansReceived, myOpenRequests });
    } catch (err) {
      console.error('Error fetching money summary:', err);
      setSummary({ loansGiven: [], loansReceived: [], myOpenRequests: [] });
    } finally {
      setLoading(false);
    }
  }, [token]); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    fetchSummary();
  }, [fetchSummary]);

  const loanKey = (loan, idx) => loan?.id || loan?._id || String(idx);

  const toggleLoanDetails = (id) => {
    setExpandedLoans((prev) => ({
      ...(Object.keys(prev).reduce((acc, k) => ({ ...acc, [k]: false }), {})),
      [id]: !prev[id],
    }));
  };

  const payNext = async (loan) => {
    const key = loanKey(loan);
    const amount = loan?.installmentAmount ?? 0;
    if (!window.confirm(`Pay the next installment (${fmtMoney(amount)})?`)) return;
    try {
      setPayLoading((m) => ({ ...m, [key]: true }));
      const res = await fetch(`/api/loans/${loan.id || loan._id}/pay-next`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...authHeader,
        },
        credentials: 'include',
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Payment failed');
      const paid = typeof data.amount === 'number' ? data.amount : amount;
      alert(`Payment successful! Amount: ${fmtMoney(paid)}`);
      await fetchSummary();
    } catch (e) {
      alert(e.message);
    } finally {
      setPayLoading((m) => ({ ...m, [key]: false }));
    }
  };

  /** ----------------------- Card renderers (one-line) ----------------------- */

  // Loans Given / Loans Received
  const renderLoanCard = (loan, isLender, idx) => {
    const key = loanKey(loan, idx);
    const isExpanded = !!expandedLoans[key];
    const otherParty = isLender ? loan.borrowerName : loan.lenderName;

    const canPay =
      !isLender &&
      typeof loan.installmentAmount === 'number' &&
      loan.installmentAmount > 0 &&
      !!loan.nextDueDate;

    const badgeClass =
      (loan.status || '').toUpperCase() === 'FUNDED'
        ? 'ms-badge ms-badge--ok'
        : 'ms-badge ms-badge--warn';

    const fundedDate =
      loan.fundedDate ? new Date(loan.fundedDate).toLocaleDateString() : 'â€”';

    return (
      <div className="ms-card" key={key}>
        {/* ONE-LINE TOP ROW */}
        <div className="ms-card-row" style={ROW_GRID}>
          <div>
            <div className="ms-label">{isLender ? 'Borrower' : 'Lender'}</div>
            <div className="ms-value-strong">{otherParty || 'Unknown'}</div>
          </div>
          <div>
            <div className="ms-label">Amount</div>
            <div className="ms-value-strong">{fmtMoney(loan.amount)}</div>
          </div>
          <div>
            <div className="ms-label">Funded Date</div>
            <div className="ms-value-strong">{fundedDate}</div>
          </div>

          <div style={{ justifySelf: 'end' }}>
            <span className={badgeClass}>{loan.status || 'â€”'}</span>
          </div>

          <div style={{ justifySelf: 'end', display: 'flex', gap: 8 }}>
            <button
              onClick={() => setThreadLoanId(loan.id || loan._id)}
              className="action-btn"
              title="Open conversation"
            >
              ðŸ’¬ Messages
            </button>
            <button
              onClick={() => toggleLoanDetails(key)}
              className="action-btn"
              title="View contract"
            >
              {isExpanded ? 'ðŸ”½ Hide' : 'ðŸ“„ View'}
            </button>
          </div>
        </div>

        {/* DETAILS */}
        {isExpanded && (
          <div className="ms-details">
            {loan.status && (
              <p className="mt-6">
                <strong>Status:</strong> {loan.status}
              </p>
            )}
            <p className="mt-6">
              <strong>Interest Rate:</strong>{' '}
              {typeof loan.interestRate === 'number' ? `${loan.interestRate}%` : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Duration:</strong>{' '}
              {typeof loan.duration === 'number' ? `${loan.duration} months` : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Next Due Date:</strong>{' '}
              {loan.nextDueDate ? new Date(loan.nextDueDate).toLocaleDateString() : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Installment (this period):</strong> {fmtMoney(loan.installmentAmount)}
            </p>

            {isLender && typeof loan.amountCollected === 'number' && (
              <p className="mt-6">
                <strong>Amount collected on loan:</strong> {fmtMoney(loan.amountCollected)}
              </p>
            )}

            {!isLender && canPay && (
              <div className="mt-6">
                <button
                  className="action-btn primary"
                  onClick={() => payNext(loan)}
                  disabled={!!payLoading[key]}
                  title="Pay next installment"
                >
                  {payLoading[key] ? 'Processingâ€¦' : 'ðŸ’³ Make Payment'}
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  // My Open Loan Requests (market + direct)
  const renderOpenRequestCard = (req) => {
    const key = req.id;
    const isExpanded = !!expandedLoans[key];

    return (
      <div className="ms-card" key={req.id}>
        {/* ONE-LINE TOP ROW */}
        <div className="ms-card-row" style={ROW_GRID}>
          <div>
            <div className="ms-label">Borrower</div>
            <div className="ms-value-strong">{req.borrowerName || 'You'}</div>
          </div>
          <div>
            <div className="ms-label">Amount</div>
            <div className="ms-value-strong">{fmtMoney(req.amount)}</div>
          </div>
          <div>
            <div className="ms-label">Created</div>
            <div className="ms-value-strong">
              {req.createdAt ? new Date(req.createdAt).toLocaleDateString() : 'â€”'}
            </div>
          </div>

          <div style={{ justifySelf: 'end' }}>
            <span className="ms-badge ms-badge--warn">Open</span>
          </div>

          <div style={{ justifySelf: 'end' }}>
            <button
              className="action-btn"
              onClick={() => toggleLoanDetails(key)}
              title="View request details"
            >
              {isExpanded ? 'ðŸ”½ Hide' : 'ðŸ“„ View'}
            </button>
          </div>
        </div>

        {/* DETAILS (you can add negotiation UI here) */}
        {isExpanded && (
          <div className="ms-details">
            <p className="mt-6">
              <strong>Status:</strong> {req.status ?? 'OPEN'}
            </p>
            <p className="mt-6">
              <strong>Interest Rate:</strong>{' '}
              {Number.isFinite(req.interestRate) ? `${req.interestRate}%` : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Duration:</strong>{' '}
              {Number.isFinite(req.duration) ? `${req.duration} months` : 'â€”'}
            </p>
            {req.purpose ? (
              <p className="mt-6">
                <strong>Reason:</strong> {req.purpose}
              </p>
            ) : null}
            {req.lenderName ? (
              <p className="mt-6">
                <strong>Requested Lender:</strong> {req.lenderName}
              </p>
            ) : null}

            {/* Placeholder: negotiate/accept UI (wire to your endpoints) */}
            {/* <div className="mt-6" style={{ display: 'flex', gap: 8 }}>
              <button className="action-btn">Make Counter-Offer</button>
              <button className="action-btn primary">Accept</button>
            </div> */}
          </div>
        )}
      </div>
    );
  };

  /** ------------------------------- Render ------------------------------- */

  if (loading) return <p className="ms-loading ms-container">Loading money summary...</p>;

  const given = Array.isArray(summary.loansGiven) ? summary.loansGiven : [];
  const received = Array.isArray(summary.loansReceived) ? summary.loansReceived : [];
  const myOpen = Array.isArray(summary.myOpenRequests) ? summary.myOpenRequests : [];

  return (
    <div className="ms-container">
      <h2 className="ms-heading">ðŸ’µ My Money Summary</h2>

      {/* Section: Open Requests */}
      <div className="ms-section">
        <button
          className="ms-section-head"
          onClick={() => setOpenSec((s) => ({ ...s, open: !s.open }))}
          aria-expanded={openSec.open}
        >
          <div className="ms-subheading">My Open Loan Requests</div>
          <div className="ms-caret">{openSec.open ? 'â–¾' : 'â–¸'}</div>
        </button>
        {openSec.open && (
          <div className="ms-section-body">
            {myOpen.length === 0 ? (
              <p className="ms-empty">You have no open requests.</p>
            ) : (
              myOpen.map(renderOpenRequestCard)
            )}
          </div>
        )}
      </div>

      {/* Section: Loans Given */}
      <div className="ms-section">
        <button
          className="ms-section-head"
          onClick={() => setOpenSec((s) => ({ ...s, given: !s.given }))}
          aria-expanded={openSec.given}
        >
          <div className="ms-subheading">Loans Given (I'm the Lender)</div>
          <div className="ms-caret">{openSec.given ? 'â–¾' : 'â–¸'}</div>
        </button>
        {openSec.given && (
          <div className="ms-section-body">
            {given.length === 0 ? (
              <p className="ms-empty">No loans given yet.</p>
            ) : (
              given.map((loan, idx) => renderLoanCard(loan, true, idx))
            )}
          </div>
        )}
      </div>

      {/* Section: Loans Received */}
      <div className="ms-section">
        <button
          className="ms-section-head"
          onClick={() => setOpenSec((s) => ({ ...s, received: !s.received }))}
          aria-expanded={openSec.received}
        >
          <div className="ms-subheading">Loans Received (I'm the Borrower)</div>
          <div className="ms-caret">{openSec.received ? 'â–¾' : 'â–¸'}</div>
        </button>
        {openSec.received && (
          <div className="ms-section-body">
            {received.length === 0 ? (
              <p className="ms-empty">No loans received yet.</p>
            ) : (
              received.map((loan, idx) => renderLoanCard(loan, false, idx))
            )}
          </div>
        )}
      </div>

      {profileUserId && (
        <UserProfileModal userId={profileUserId} onClose={() => setProfileUserId(null)} />
      )}

      {threadLoanId && (
        <LoanThreadModal loanId={threadLoanId} onClose={() => setThreadLoanId(null)} />
      )}
    </div>
  );
};

export default MoneySummary;
