// src/features/wallet/WalletPanel.jsx
import React, { useState } from 'react';
import { CardElement, useElements, useStripe } from '@stripe/react-stripe-js';
import { createDepositIntent, fetchWallet } from './walletApi';

export default function WalletPanel({ onClose, onBalanceUpdated }) {
  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);

  const stripe = useStripe();
  const elements = useElements();

  // Parse amount text -> dollars, minimum $1
  const parsed = Number((amount || '').replace(/[^0-9.]/g, ''));
  const amountDollars = Math.max(1, Number.isFinite(parsed) ? parsed : 0);

  async function deposit() {
    try {
      setBusy(true);

      // 1) Ask backend for a PaymentIntent (or a simulated deposit)
      const { clientSecret, simulated } = await createDepositIntent({ amountDollars });

      // If backend is in "simulated" mode (no Stripe key), just refresh wallet
      if (simulated) {
        const w = await fetchWallet();
        onBalanceUpdated?.(w.availableCents);
        onClose?.();
        return;
      }

      // At this point we expect a real Stripe clientSecret
      if (!clientSecret) {
        alert('Missing client secret from server. Please try again.');
        return;
      }

      if (!stripe || !elements) {
        alert('Stripe is not ready yet. Please try again.');
        return;
      }

      const cardElement = elements.getElement(CardElement);
      if (!cardElement) {
        alert('Card input not ready.');
        return;
      }

      // 2) Confirm the card payment on the client
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement },
      });

      if (result.error) {
        console.error(result.error);
        alert(result.error.message || 'Deposit failed');
        return;
      }

      // 3) Refresh wallet balance after successful payment
      const w = await fetchWallet();
      onBalanceUpdated?.(w.availableCents);
      onClose?.();
    } catch (e) {
      console.error('Deposit failed:', e);
      alert(e.message || 'Deposit failed');
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="wallet-panel">
      <h3>Add funds</h3>

      <div className="row">
        <label>Amount</label>
        <div className="input-wrap">
          <span>$</span>
          <input
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
          />
        </div>
      </div>

      <div className="row" style={{ marginTop: 12 }}>
        <label>Card details</label>
        <div
          style={{
            padding: 8,
            borderRadius: 8,
            border: '1px solid #e2e8f0',
            background: '#fff',
          }}
        >
          <CardElement />
        </div>
      </div>

      <div className="row right" style={{ marginTop: 16 }}>
        <button className="btn" onClick={onClose} disabled={busy}>
          Cancel
        </button>
        <button className="btn primary" onClick={deposit} disabled={busy}>
          {busy ? 'Processing…' : 'Continue'}
        </button>
      </div>
    </div>
  );
}
