import React, { useEffect, useState } from 'react';
import './AdminDashboard.css'; // optional, can be empty for now

const getToken = () => localStorage.getItem('token') || '';

const AdminDashboard = () => {
  const [tab, setTab] = useState('verification'); // 'users' | 'verification'

  // --- Users tab state ---
  const [users, setUsers] = useState([]);
  const [usersLoading, setUsersLoading] = useState(false);
  const [usersError, setUsersError] = useState('');
  const [userSearch, setUserSearch] = useState('');
  const [userSuperOnly, setUserSuperOnly] = useState(false);

  // --- Verification tab state ---
  const [pendingUsers, setPendingUsers] = useState([]);
  const [verifyListLoading, setVerifyListLoading] = useState(false);
  const [verifyListError, setVerifyListError] = useState('');

  const [selectedUserId, setSelectedUserId] = useState(null);
  const [selectedDetail, setSelectedDetail] = useState(null);
  const [detailLoading, setDetailLoading] = useState(false);
  const [detailError, setDetailError] = useState('');
  const [actionLoading, setActionLoading] = useState(false);
  const [actionMessage, setActionMessage] = useState('');

  const token = getToken();

  /* ------------------------- USERS TAB LOGIC ------------------------- */

  const fetchUsers = async () => {
    if (!token) return;
    try {
      setUsersLoading(true);
      setUsersError('');

      const params = new URLSearchParams();
      if (userSearch) params.set('q', userSearch);
      if (userSuperOnly) params.set('superOnly', 'true');

      const res = await fetch(`/api/admin/users?${params.toString()}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || 'Failed to load users');
      }

      const data = await res.json();
      setUsers(data);
    } catch (err) {
      console.error('Admin fetchUsers error:', err);
      setUsersError('Failed to load users.');
    } finally {
      setUsersLoading(false);
    }
  };

  /* --------------------- VERIFICATION TAB LOGIC ---------------------- */

  const fetchPendingVerifications = async () => {
    if (!token) return;
    try {
      setVerifyListLoading(true);
      setVerifyListError('');
      setPendingUsers([]);
      setSelectedUserId(null);
      setSelectedDetail(null);
      setDetailError('');
      setActionMessage('');

      const res = await fetch('/api/verification/admin/verification/pending', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || 'Failed to load pending verifications');
      }

      const data = await res.json();
      setPendingUsers(data);
    } catch (err) {
      console.error('fetchPendingVerifications error:', err);
      setVerifyListError('Failed to load pending verifications.');
    } finally {
      setVerifyListLoading(false);
    }
  };

  const fetchVerificationDetail = async (userId) => {
    if (!token || !userId) return;
    try {
      setSelectedUserId(userId);
      setDetailLoading(true);
      setDetailError('');
      setSelectedDetail(null);
      setActionMessage('');

      const res = await fetch(`/api/verification/admin/verification/${userId}/detail`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || 'Failed to load verification detail');
      }

      const data = await res.json();
      setSelectedDetail(data);
    } catch (err) {
      console.error('fetchVerificationDetail error:', err);
      setDetailError('Failed to load verification detail.');
    } finally {
      setDetailLoading(false);
    }
  };

  const handleApprove = async () => {
    if (!selectedUserId || !token) return;
    try {
      setActionLoading(true);
      setActionMessage('');
      setDetailError('');

      const res = await fetch(
        `/api/verification/admin/verification/${selectedUserId}/approve`,
        {
          method: 'POST',
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const text = await res.text();
      if (!res.ok) {
        throw new Error(text || 'Failed to approve user');
      }

      setActionMessage('User approved successfully.');
      // Refresh pending list
      await fetchPendingVerifications();
      setSelectedUserId(null);
      setSelectedDetail(null);
    } catch (err) {
      console.error('handleApprove error:', err);
      setDetailError(err.message || 'Failed to approve user.');
    } finally {
      setActionLoading(false);
    }
  };

  const handleReject = async () => {
    if (!selectedUserId || !token) return;
    const reason = window.prompt('Reason for rejection? (optional)', '');
    try {
      setActionLoading(true);
      setActionMessage('');
      setDetailError('');

      const res = await fetch(
        `/api/verification/admin/verification/${selectedUserId}/reject`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ reason }),
        }
      );

      const text = await res.text();
      if (!res.ok) {
        throw new Error(text || 'Failed to reject user');
      }

      setActionMessage('User rejected.');
      // Refresh pending list
      await fetchPendingVerifications();
      setSelectedUserId(null);
      setSelectedDetail(null);
    } catch (err) {
      console.error('handleReject error:', err);
      setDetailError(err.message || 'Failed to reject user.');
    } finally {
      setActionLoading(false);
    }
  };

  /* ------------------------------ EFFECTS ---------------------------- */

  // Load tab data on tab change
  useEffect(() => {
    if (tab === 'users') {
      fetchUsers();
    } else if (tab === 'verification') {
      fetchPendingVerifications();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [tab]);

  /* --------------------------- RENDER HELPERS ------------------------ */

  const prettyType = (t) => {
    switch (t) {
      case 'ID_FRONT':
        return 'Front of ID';
      case 'ID_BACK':
        return 'Back of ID';
      case 'SELFIE':
        return 'Selfie';
      default:
        return t;
    }
  };

  const renderDocPreview = (doc) => {
    // This assumes you have a /api/documents/:id route that streams content.
    // If mimeType is image/*, we display an <img>; otherwise we show a link.
    const isImage = doc.mimeType && doc.mimeType.startsWith('image/');
    const src = `/api/documents/${doc.id}`;

    if (isImage) {
      return (
        <div className="adm-doc-card" key={doc.id}>
          <div className="adm-doc-label">{prettyType(doc.type)}</div>
          <img
            src={src}
            alt={doc.title || doc.type}
            className="adm-doc-img"
          />
        </div>
      );
    }

    return (
      <div className="adm-doc-card" key={doc.id}>
        <div className="adm-doc-label">{prettyType(doc.type)}</div>
        <a href={src} target="_blank" rel="noreferrer">
          Open document
        </a>
      </div>
    );
  };

  /* ------------------------------- JSX ------------------------------- */

  return (
    <div className="admin-shell">
      <header className="admin-header">
        <h1>PeerFund Admin</h1>
        <nav className="admin-tabs">
          <button
            className={tab === 'users' ? 'active' : ''}
            onClick={() => setTab('users')}
          >
            Users
          </button>
          <button
            className={tab === 'verification' ? 'active' : ''}
            onClick={() => setTab('verification')}
          >
            Verification
          </button>
        </nav>
      </header>

      {/* USERS TAB */}
      {tab === 'users' && (
        <section className="admin-panel">
          <div className="admin-toolbar">
            <form
              onSubmit={(e) => {
                e.preventDefault();
                fetchUsers();
              }}
              className="admin-search"
            >
              <input
                type="text"
                placeholder="Search by name or email"
                value={userSearch}
                onChange={(e) => setUserSearch(e.target.value)}
              />
              <button type="submit">Search</button>
            </form>
            <label className="admin-checkbox">
              <input
                type="checkbox"
                checked={userSuperOnly}
                onChange={(e) => setUserSuperOnly(e.target.checked)}
              />
              SuperUsers only
            </label>
          </div>

          {usersLoading && <div>Loading usersâ€¦</div>}
          {usersError && <div className="admin-error">{usersError}</div>}

          {!usersLoading && !usersError && (
            <div className="admin-table-wrapper">
              <table className="admin-table">
                <thead>
                  <tr>
                    <th>Created</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Role</th>
                    <th>SuperUser</th>
                    <th>Subscription</th>
                  </tr>
                </thead>
                <tbody>
                  {users.length === 0 ? (
                    <tr>
                      <td colSpan={6} style={{ textAlign: 'center' }}>
                        No users found.
                      </td>
                    </tr>
                  ) : (
                    users.map((u) => (
                      <tr key={u.id}>
                        <td>{new Date(u.createdAt).toLocaleString()}</td>
                        <td>{u.name || 'â€”'}</td>
                        <td>{u.email}</td>
                        <td>{u.role}</td>
                        <td>{u.isSuperUser ? 'Yes' : 'No'}</td>
                        <td>{u.subscriptionStatus || 'â€”'}</td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </section>
      )}

      {/* VERIFICATION TAB */}
      {tab === 'verification' && (
        <section className="admin-panel admin-verification">
          <div className="admin-verification-layout">
            {/* Left: pending list */}
            <div className="adm-verify-list">
              <div className="adm-panel-head">
                <h2>Pending verifications</h2>
                <button
                  type="button"
                  onClick={fetchPendingVerifications}
                  disabled={verifyListLoading}
                >
                  Refresh
                </button>
              </div>

              {verifyListLoading && <div>Loading pending usersâ€¦</div>}
              {verifyListError && (
                <div className="admin-error">{verifyListError}</div>
              )}

              {!verifyListLoading && !verifyListError && (
                <ul className="adm-list">
                  {pendingUsers.length === 0 ? (
                    <li className="adm-empty">No pending verifications ðŸŽ‰</li>
                  ) : (
                    pendingUsers.map((u) => (
                      <li
                        key={u.id}
                        className={
                          'adm-list-item' +
                          (selectedUserId === u.id ? ' selected' : '')
                        }
                        onClick={() => fetchVerificationDetail(u.id)}
                      >
                        <div className="adm-list-name">{u.name || 'â€”'}</div>
                        <div className="adm-list-email">{u.email}</div>
                        <div className="adm-list-date">
                          Joined {new Date(u.createdAt).toLocaleDateString()}
                        </div>
                      </li>
                    ))
                  )}
                </ul>
              )}
            </div>

            {/* Right: detail view */}
            <div className="adm-verify-detail">
              {!selectedUserId && (
                <div className="adm-placeholder">
                  Select a user on the left to review their documents.
                </div>
              )}

              {selectedUserId && (
                <>
                  {detailLoading && <div>Loading detailâ€¦</div>}
                  {detailError && (
                    <div className="admin-error">{detailError}</div>
                  )}
                  {actionMessage && (
                    <div className="admin-success">{actionMessage}</div>
                  )}

                  {selectedDetail && !detailLoading && (
                    <div className="adm-detail-inner">
                      <div className="adm-detail-head">
                        <div>
                          <h2>{selectedDetail.user.name || 'Unnamed user'}</h2>
                          <div className="adm-detail-email">
                            {selectedDetail.user.email}
                          </div>
                          <div className="adm-detail-status">
                            Status: {selectedDetail.user.verificationStatus}
                          </div>
                          {selectedDetail.user.verificationNotes && (
                            <div className="adm-detail-notes">
                              Notes: {selectedDetail.user.verificationNotes}
                            </div>
                          )}
                        </div>
                      </div>

                      <div className="adm-docs-grid">
                        {selectedDetail.docs && selectedDetail.docs.length > 0 ? (
                          selectedDetail.docs.map(renderDocPreview)
                        ) : (
                          <div>No documents found for this user.</div>
                        )}
                      </div>

                      <div className="adm-actions">
                        <button
                          type="button"
                          className="btn-danger"
                          onClick={handleReject}
                          disabled={actionLoading}
                        >
                          Reject
                        </button>
                        <button
                          type="button"
                          className="btn-primary"
                          onClick={handleApprove}
                          disabled={actionLoading}
                        >
                          Approve
                        </button>
                      </div>
                    </div>
                  )}
                </>
              )}
            </div>
          </div>
        </section>
      )}
    </div>
  );
};

export default AdminDashboard;
