// src/features/wallet/WalletPanel.jsx
import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { createDepositIntent, fetchWallet } from './walletApi';

const stripePromise = loadStripe(
  import.meta.env?.VITE_STRIPE_PUBLISHABLE_KEY || process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
);

export default function WalletPanel({ onClose, onBalanceUpdated }) {
  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);

  const cleanAmount = amount.replace(/[^0-9.]/g, '');
  const dollars = Number(cleanAmount || 0);

  const deposit = async () => {
    try {
      setBusy(true);

      const { clientSecret, simulated } = await createDepositIntent({
        amountDollars: dollars,
      });

      if (!simulated && clientSecret) {
        const stripe = await stripePromise;
        const result = await stripe.confirmCardPayment(clientSecret);
        if (result.error) throw new Error(result.error.message);
      }

      const w = await fetchWallet();
      onBalanceUpdated?.(w.availableCents);
      onClose?.();
    } catch (e) {
      console.error('Deposit failed:', e);
      alert(e.message || 'Deposit failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="wallet-panel">
      <h3>Add funds</h3>
      <div className="row">
        <label>Amount</label>
        <div className="input-wrap">
          <span>$</span>
          <input
            value={amount}
            onChange={e => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
          />
        </div>
      </div>
      <div className="row right">
        <button className="btn" onClick={onClose}>Cancel</button>
        <button className="btn primary" onClick={deposit} disabled={busy}>
          Continue
        </button>
      </div>
    </div>
  );
}
