// src/features/wallet/walletApi.js
const authHeader = () => {
  const t = localStorage.getItem('token');
  return t ? { Authorization: `Bearer ${t}` } : {};
};

export async function fetchWallet() {
  const r = await fetch('/api/wallet/me', { headers: { ...authHeader() } });
  if (!r.ok) throw new Error(await r.text());
  return r.json(); // { available, pending, ledger }
}

export async function createDepositIntent(amountCents) {
  const r = await fetch('/api/wallet/deposit-intent', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json', ...authHeader() },
    body: JSON.stringify({ amountCents }),
  });
  const data = await r.json().catch(() => ({}));
  if (!r.ok) throw new Error(data?.error || 'Failed to create intent');
  return data; // { clientSecret, intentId, amountCents }
}

// Dev-only confirm (without Stripe webhook)
export async function confirmDepositDev(intentId) {
  const r = await fetch('/api/wallet/deposit/confirm', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json', ...authHeader() },
    body: JSON.stringify({ intentId }),
  });
  const data = await r.json().catch(() => ({}));
  if (!r.ok) throw new Error(data?.error || 'Failed to confirm deposit');
  return data;
}
