// src/features/auth/VerifiedRoute.jsx
import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';

const VerifiedRoute = ({ children }) => {
  const [status, setStatus] = useState('LOADING'); // LOADING | APPROVED | PENDING | REJECTED | ERROR
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setStatus('ERROR');
      setError('Not authenticated');
      return;
    }

    const fetchStatus = async () => {
      try {
        const res = await fetch('/api/verification/status', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.ok) {
          const text = await res.text();
          throw new Error(text || 'Failed to fetch verification status');
        }

        const data = await res.json();
        // expect data.status from getVerificationChecklist
        if (data.status === 'APPROVED') {
          setStatus('APPROVED');
        } else if (data.status === 'REJECTED') {
          setStatus('REJECTED');
        } else {
          // treat anything else as pending / missing docs
          setStatus('PENDING');
        }
      } catch (err) {
        console.error('VerifiedRoute error:', err);
        setStatus('ERROR');
        setError(err.message || 'Failed to check verification');
      }
    };

    fetchStatus();
  }, []);

  if (status === 'LOADING') {
    return (
      <div style={{ padding: 24 }}>
        Checking your verification status…
      </div>
    );
  }

  // If not verified, send them to /verify
  if (status === 'PENDING' || status === 'REJECTED') {
    return <Navigate to="/verify" replace />;
  }

  // If some error happened, you can also boot to /verify or /login.
  if (status === 'ERROR') {
    console.warn('VerifiedRoute error:', error);
    return <Navigate to="/verify" replace />;
  }

  // APPROVED → allow access
  return children;
};

export default VerifiedRoute;
