import React, { useEffect, useState } from 'react';
import WalletPanel from './WalletPanel';
import { fetchWallet } from './walletApi';

export default function WalletPage() {
  const [wallet, setWallet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showPanel, setShowPanel] = useState(false);

  async function loadWallet() {
    try {
      const w = await fetchWallet();
      setWallet(w);
    } catch (e) {
      console.error('Failed to load wallet:', e);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    loadWallet();
  }, []);

  const availableDollars =
    wallet?.availableCents != null
      ? (wallet.availableCents / 100).toFixed(2)
      : '0.00';

  const pendingDollars =
    wallet?.pendingCents != null
      ? (wallet.pendingCents / 100).toFixed(2)
      : '0.00';

  return (
    <div style={{ padding: '2rem', maxWidth: 720, margin: '0 auto' }}>
      <h2 style={{ marginTop: 0 }}>Wallet</h2>
      <p style={{ color: '#475569', marginBottom: '1.5rem' }}>
        Add money to your PeerFund wallet so you can quickly fund loans.
      </p>

      {loading ? (
        <p>Loading walletâ€¦</p>
      ) : (
        <div
          style={{
            border: '1px solid #e2e8f0',
            borderRadius: 12,
            padding: '14px 16px',
            background: '#ffffff',
            marginBottom: 16,
          }}
        >
          <div style={{ fontSize: 14, color: '#64748b', marginBottom: 4 }}>
            Current balance
          </div>
          <div style={{ fontSize: 28, fontWeight: 800, color: '#0f172a' }}>
            ${availableDollars}
          </div>
          <div style={{ fontSize: 13, color: '#6b7280', marginTop: 4 }}>
            Pending holds: ${pendingDollars}
          </div>

          <div style={{ marginTop: 16 }}>
            <button
              onClick={() => setShowPanel(true)}
              style={{
                background: '#4f46e5',
                border: '1px solid #4f46e5',
                color: '#fff',
                fontWeight: 700,
                padding: '8px 14px',
                borderRadius: 10,
                cursor: 'pointer',
              }}
            >
              Add funds
            </button>
          </div>
        </div>
      )}

      {/* Slide-over / modal for choosing amount */}
      {showPanel && (
        <div
          style={{
            position: 'fixed',
            inset: 0,
            background: 'rgba(15,23,42,0.35)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 40,
          }}
        >
          <div
            style={{
              background: '#fff',
              borderRadius: 12,
              padding: 16,
              minWidth: 320,
              maxWidth: 420,
              boxShadow: '0 10px 40px rgba(15,23,42,0.18)',
            }}
          >
            <WalletPanel
              onClose={() => setShowPanel(false)}
              onBalanceUpdated={(newAvailableCents) => {
                setWallet((prev) =>
                  prev
                    ? { ...prev, availableCents: newAvailableCents }
                    : { availableCents: newAvailableCents, pendingCents: 0 }
                );
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
}
