// src/features/wallet/PaymentMethod.jsx
import { useEffect, useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, useStripe } from '@stripe/react-stripe-js';

import { fetchWallet, createDepositIntent, devConfirmDeposit } from '../wallet/walletApi';
import ConnectPayoutsCard from '../wallet/ConnectPayoutsCard';

/* ---------------- Stripe publishable key ---------------- */
const rawPk = (process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || '').trim();
const pk = /^pk_(test|live)_[A-Za-z0-9_]+$/.test(rawPk) ? rawPk : '';

/* ---------------- Auth header helpers ---------------- */
function getToken() {
  let t = localStorage.getItem('token') || '';
  t = t.replace(/^"|"$/g, '');
  if (t.toLowerCase().startsWith('bearer ')) t = t.slice(7);
  return t || null;
}
function authHeaders() {
  const t = getToken();
  return t ? { Authorization: `Bearer ${t}` } : {};
}

/* ---------------- Stripe init (cached) ---------------- */
let cachedStripePromise;
function getStripePromise() {
  if (!pk) return null;
  if (!cachedStripePromise) cachedStripePromise = loadStripe(pk);
  return cachedStripePromise;
}

/* ================================================================== */
/*                           Payment Method                           */
/* ================================================================== */

export default function PaymentMethodPage() {
  const [clientSecret, setClientSecret] = useState(null);
  const [hasDefault, setHasDefault] = useState(false);
  const [linking, setLinking] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [loadingStatus, setLoadingStatus] = useState(true);

  // deposit UI
  const [amount, setAmount] = useState('25.00');
  const [depositing, setDepositing] = useState(false);
  const [wallet, setWallet] = useState(null);

  // Prefilled identity
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');

  // Saved PMs
  const [myPMs, setMyPMs] = useState([]);

  const stripePromise = useMemo(getStripePromise, [pk]);

  useEffect(() => {
    const ac = new AbortController();
    (async () => {
      try {
        // Profile prefill
        try {
          const r = await fetch('/api/users/profile', { headers: { ...authHeaders() }, signal: ac.signal });
          if (r.ok) {
            const u = await r.json();
            if (u?.name) setFullName(u.name);
            if (u?.email) setEmail(u.email);
          }
        } catch {}

        await refreshMyPaymentMethods(ac.signal);

        try {
          const w = await fetchWallet();
          setWallet(w);
        } catch {}
      } finally {
        setLoadingStatus(false);
      }
    })();
    return () => ac.abort();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  async function refreshMyPaymentMethods(signal) {
    try {
      const r = await fetch('/api/payment-method/mine', {
        headers: { ...authHeaders() },
        signal,
      });
      if (r.ok) {
        const d = await r.json();
        const items = Array.isArray(d?.items) ? d.items : [];
        setMyPMs(items);
        setHasDefault(items.some((it) => !!it.isDefault));
      } else {
        setMyPMs([]);
        setHasDefault(false);
      }
    } catch {
      setMyPMs([]);
      setHasDefault(false);
    }
  }

  async function refreshWallet() {
    try {
      const w = await fetchWallet();
      setWallet(w);
    } catch {}
  }

  async function startBankLink() {
    setLinking(true);
    setErrorMsg('');
    try {
      // Backend issues a SetupIntent client_secret (ACH)
      const r = await fetch('/api/stripe/create-bank-setup-intent', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
      });

      if (r.status === 401) {
        setErrorMsg('Your session expired. Please log in again, then return to Payment Method.');
        return;
      }
      if (!r.ok) throw new Error((await r.text()) || 'Failed to create SetupIntent.');

      const { client_secret } = await r.json();
      if (!client_secret) throw new Error('No client_secret returned from server.');

      setClientSecret(client_secret);
    } catch (e) {
      setErrorMsg(e.message || 'Something went wrong starting bank link.');
    } finally {
      setLinking(false);
    }
  }

  const disabledLinkButton = linking || !pk || !stripePromise || !fullName;

  /* ---------- Actions on existing PMs ---------- */
  async function setDefault(id) {
    try {
      setErrorMsg('');
      const r = await fetch('/api/payment-method/set-default', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
        body: JSON.stringify({ id }),
      });
      if (!r.ok) throw new Error((await r.text()) || 'Failed to set default');
      await refreshMyPaymentMethods();
    } catch (e) {
      setErrorMsg(e.message || 'Failed to set default.');
    }
  }

  async function setLoanBank(id) {
    try {
      setErrorMsg('');
      const r = await fetch('/api/payment-method/set-loan-bank', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
        body: JSON.stringify({ id }),
      });
      if (!r.ok) throw new Error((await r.text()) || 'Failed to set loan bank');
      await refreshMyPaymentMethods();
    } catch (e) {
      setErrorMsg(e.message || 'Failed to set loan bank.');
    }
  }

  return (
    <div style={{ maxWidth: 640, margin: '0 auto', padding: 16 }}>
      <h2 style={{ marginTop: 0 }}>Payment Method</h2>

      {/* Express payouts connect (optional, if you use it) */}
      <ConnectPayoutsCard />

      {!pk && (
        <div style={{ background: '#fef2f2', border: '1px solid #fecaca', color: '#7f1d1d',
                      padding: '10px 12px', borderRadius: 8, marginBottom: 12 }}>
          Missing or invalid Stripe publishable key.<br />
          Add <code>REACT_APP_STRIPE_PUBLISHABLE_KEY=pk_test_…</code> to <code>.env</code> and restart the dev server.
        </div>
      )}

      {errorMsg && (
        <div style={{ background: '#fff7ed', border: '1px solid #fed7aa', color: '#7c2d12',
                      padding: '10px 12px', borderRadius: 8, marginBottom: 12 }}>
          {errorMsg}
        </div>
      )}

      {wallet && (
        <div style={{ background: '#f1f5f9', border: '1px solid #e2e8f0', padding: 10,
                      borderRadius: 8, marginBottom: 12, fontSize: 14 }}>
          <strong>Wallet</strong> — Available:{' '}
          ${(((wallet.availableCents ?? 0) / 100).toFixed(2))} • Pending:{' '}
          ${(((wallet.pendingCents ?? 0) / 100).toFixed(2))}
        </div>
      )}

      {loadingStatus ? (
        <p>Checking your payment method…</p>
      ) : (
        <>
          <p>{hasDefault ? 'Bank account on file for ACH debits.' : 'No bank account on file yet.'}</p>

          {/* Saved methods list with actions */}
          {myPMs.length > 0 && (
            <div style={{ marginBottom: 12 }}>
              <div style={{ fontWeight: 600, marginBottom: 6 }}>Your bank methods</div>
              <div style={{ display: 'grid', gap: 6 }}>
                {myPMs.map((pm) => (
                  <div
                    key={pm.id}
                    style={{
                      border: '1px solid #e2e8f0',
                      borderRadius: 8,
                      padding: '10px 12px',
                      display: 'grid',
                      gridTemplateColumns: '1fr auto',
                      alignItems: 'center',
                      gap: 8,
                      background: '#fff',
                    }}
                  >
                    <div>
                      <div style={{ fontWeight: 700 }}>{pm.brand || 'Bank'}</div>
                      <div style={{ fontSize: 13, color: '#475569' }}>
                        {pm.last4 ? `•••• ${pm.last4}` : '—'}
                      </div>
                      <div style={{ display: 'flex', gap: 6, marginTop: 6 }}>
                        {pm.isDefault && <span style={badgeStyle('#e0e7ff', '#3730a3')}>Default</span>}
                        {pm.isForLoans && <span style={badgeStyle('#dcfce7', '#166534')}>Loan Payout</span>}
                      </div>
                    </div>
                    <div style={{ display: 'flex', gap: 8 }}>
                      {!pm.isDefault && (
                        <button
                          onClick={() => setDefault(pm.id)}
                          style={btnGhost}
                          title="Set as default for deposits/repayments"
                        >
                          Set Default
                        </button>
                      )}
                      {!pm.isForLoans && (
                        <button
                          onClick={() => setLoanBank(pm.id)}
                          style={btnGhost}
                          title="Use this bank to receive loan payouts"
                        >
                          Use for Loans
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Link bank (when none) */}
          {!hasDefault && (
            <>
              <div style={{
                display: 'grid', gap: 8, margin: '8px 0 12px',
                background: '#fafafa', border: '1px solid #e5e7eb', borderRadius: 8, padding: 10,
              }}>
                <div style={{ fontSize: 14, color: '#6b7280' }}>Account holder</div>
                <input
                  type="text"
                  value={fullName}
                  onChange={(e) => setFullName(e.target.value)}
                  placeholder="Account holder full name"
                  style={{ padding: '8px 10px', border: '1px solid #e2e8f0', borderRadius: 8 }}
                />
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Email (optional, recommended)"
                  style={{ padding: '8px 10px', border: '1px solid #e2e8f0', borderRadius: 8 }}
                />
                <small style={{ color: '#6b7280' }}>
                  We’ll pass this to Stripe only to label your bank method.
                </small>
              </div>

              <button
                onClick={startBankLink}
                disabled={disabledLinkButton}
                style={{
                  background: '#4f46e5', color: '#fff', border: '1px solid #4f46e5',
                  padding: '8px 14px', borderRadius: 8,
                  cursor: disabledLinkButton ? 'not-allowed' : 'pointer',
                }}
              >
                {linking ? 'Starting…' : 'Link Bank Account'}
              </button>
            </>
          )}

          {/* Stripe bank link + SAVE to backend */}
          {clientSecret && stripePromise && (
            <Elements stripe={stripePromise}>
              <BankLinkFlow
                clientSecret={clientSecret}
                fullName={fullName}
                email={email}
                onSaved={async () => {
                  setClientSecret(null);
                  await refreshMyPaymentMethods();
                  await refreshWallet();
                }}
                onError={setErrorMsg}
              />
            </Elements>
          )}

          {/* Deposit panel appears once a default method exists */}
          {hasDefault && (
            <div style={{
              marginTop: 20, padding: 12, borderRadius: 8,
              border: '1px solid #e2e8f0', background: '#ffffff',
            }}>
              <h3 style={{ margin: '0 0 8px' }}>Deposit to PeerFund Balance</h3>
              <p style={{ marginTop: 0, color: '#475569' }}>
                Funds will be debited from your linked bank and added to your available balance.
              </p>

              <DepositBox
                amount={amount}
                setAmount={setAmount}
                depositing={depositing}
                setDepositing={setDepositing}
                onSuccess={async () => {
                  await refreshWallet();
                  alert('Deposit initiated successfully.');
                }}
                onError={setErrorMsg}
              />
            </div>
          )}
        </>
      )}
    </div>
  );
}

function badgeStyle(bg, fg) {
  return {
    background: bg, color: fg, border: `1px solid ${bg}`,
    borderRadius: 999, padding: '2px 8px', fontSize: 12, fontWeight: 700
  };
}

const btnGhost = {
  background: '#ffffff',
  color: '#1f2937',
  border: '1px solid #e5e7eb',
  padding: '8px 10px',
  borderRadius: 8,
  cursor: 'pointer',
};

/* ---------------- BankLinkFlow: collect + confirm + SAVE ---------------- */

function BankLinkFlow({ clientSecret, fullName, email, onSaved, onError }) {
  const stripe = useStripe();
  const [working, setWorking] = useState(false);

  async function saveToBackend(paymentMethodId) {
    const r = await fetch('/api/payment-method/save', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', ...authHeaders() },
      body: JSON.stringify({
        paymentMethodId,
        makeDefault: true,   // mark as default for debits/repayments
        useForLoans: true,   // also mark as the payout bank
      }),
    });
    if (!r.ok) {
      const t = await r.text().catch(() => '');
      throw new Error(t || 'Failed to save payment method to your PeerFund account.');
    }
  }

  async function handleLink() {
    if (!stripe) { onError?.('Stripe not ready yet.'); return; }
    if (!fullName) { onError?.('Please enter the account holder’s full name.'); return; }

    setWorking(true);
    onError?.('');

    try {
      // 1) Open Financial Connections to collect bank
      const { error: collectError } = await stripe.collectBankAccountForSetup({
        clientSecret,
        params: {
          payment_method_type: 'us_bank_account',
          payment_method_data: {
            billing_details: { name: fullName, ...(email ? { email } : {}) },
          },
        },
      });
      if (collectError) throw collectError;

      // 2) Confirm SetupIntent (Stripe attaches PM to your Customer)
      const { setupIntent, error: confirmError } =
        await stripe.confirmUsBankAccountSetup(clientSecret);
      if (confirmError) throw confirmError;

      const pmId = setupIntent?.payment_method;
      if (!pmId) throw new Error('Missing payment method id from Stripe.');

      // 3) Save to your DB (per-user)
      await saveToBackend(pmId);

      // 4) Status UX
      switch (setupIntent.status) {
        case 'succeeded':
          alert('Bank account saved. ACH payments are now enabled.');
          break;
        case 'requires_action': {
          const na = setupIntent.next_action;
          if (na?.type === 'verify_with_microdeposits') {
            alert('Bank linked. Stripe is sending two micro-deposits for verification (1–2 business days).');
          } else {
            alert('Bank linked; additional verification is required.');
          }
          break;
        }
        default:
          alert(`Setup in progress (status: ${setupIntent.status}).`);
      }

      onSaved?.();
    } catch (e) {
      onError?.(e?.message || 'Failed to link bank account.');
    } finally {
      setWorking(false);
    }
  }

  return (
    <div style={{ marginTop: 12 }}>
      <button
        onClick={handleLink}
        disabled={!stripe || working}
        style={{
          background: '#10b981', color: '#fff', border: '1px solid #10b981',
          padding: '8px 14px', borderRadius: 8,
          cursor: !stripe || working ? 'not-allowed' : 'pointer',
        }}
      >
        {working ? 'Connecting…' : 'Connect and Save Bank'}
      </button>
    </div>
  );
}

BankLinkFlow.propTypes = {
  clientSecret: PropTypes.string.isRequired,
  fullName: PropTypes.string.isRequired,
  email: PropTypes.string,
  onSaved: PropTypes.func,
  onError: PropTypes.func,
};

/* -------- DepositBox: create + confirm PaymentIntent (ACH) -------- */

function DepositBox({ amount, setAmount, depositing, setDepositing, onSuccess, onError }) {
  const stripe = useStripe();

  async function handleDeposit() {
    const n = Number(amount);
    if (!Number.isFinite(n) || n <= 0) {
      onError?.('Enter a valid deposit amount.');
      return;
    }
    setDepositing(true);
    onError?.('');

    try {
      const { clientSecret, ledgerId, simulated } = await createDepositIntent(n);

      // Dev-mode simulation: backend may immediately mark as confirmed
      if (simulated) {
        await devConfirmDeposit(ledgerId);
        onSuccess?.();
        return;
      }

      if (!stripe) throw new Error('Stripe not ready.');
      const { paymentIntent, error } =
        await stripe.confirmUsBankAccountPayment(clientSecret);
      if (error) throw error;

      const status = paymentIntent?.status;
      if (status === 'succeeded' || status === 'processing') {
        onSuccess?.();
      } else if (status === 'requires_action') {
        const na = paymentIntent.next_action;
        if (na?.type === 'verify_with_microdeposits') {
          alert('Deposit initiated. Funds will appear after micro-deposit verification.');
          onSuccess?.();
        } else {
          onError?.('Additional action is required to complete this deposit.');
        }
      } else {
        onError?.(`Deposit status: ${status || 'unknown'}.`);
      }
    } catch (e) {
      onError?.(e?.message || 'Deposit failed.');
    } finally {
      setDepositing(false);
    }
  }

  return (
    <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 6 }}>
        <span>$</span>
        <input
          type="number"
          step="0.01"
          min="1"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          style={{ width: 120, padding: '8px 10px', border: '1px solid #e2e8f0', borderRadius: 8 }}
        />
      </div>
      <button
        onClick={handleDeposit}
        disabled={depositing}
        style={{
          background: '#4f46e5', color: '#fff', border: '1px solid #4f46e5',
          padding: '8px 14px', borderRadius: 8,
          cursor: depositing ? 'not-allowed' : 'pointer',
        }}
      >
        {depositing ? 'Processing…' : 'Deposit'}
      </button>
    </div>
  );
}

DepositBox.propTypes = {
  amount: PropTypes.string.isRequired,
  setAmount: PropTypes.func.isRequired,
  depositing: PropTypes.bool.isRequired,
  setDepositing: PropTypes.func,
  onSuccess: PropTypes.func,
  onError: PropTypes.func,
};
