// src/features/wallet/PaymentMethod.jsx
import { useEffect, useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, useStripe } from '@stripe/react-stripe-js';

import {
  fetchWallet,
  createDepositIntent,
  devConfirmDeposit,
} from '../wallet/walletApi';

/* ---------------- Stripe publishable key ---------------- */
const rawPk = (process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY || '').trim();
const pk = /^pk_(test|live)_[A-Za-z0-9_]+$/.test(rawPk) ? rawPk : '';

/* ---------------- Auth helpers ---------------- */
function getToken() {
  let t = localStorage.getItem('token') || '';
  t = t.replace(/^"|"$/g, '');                 // strip accidental quotes
  if (t.toLowerCase().startsWith('bearer ')) { // strip accidental 'Bearer '
    t = t.slice(7);
  }
  return t || null;
}
function authHeaders() {
  const t = getToken();
  return t ? { Authorization: `Bearer ${t}` } : {};
}
// Optional local check: warn if token seems expired
function jwtIsExpired(t) {
  try {
    const [, payload] = t.split('.');
    const { exp } = JSON.parse(atob(payload));
    return !exp || Date.now() / 1000 >= exp;
  } catch {
    return false;
  }
}

/* ---------------- Stripe init (cached) ---------------- */
let cachedStripePromise;
function getStripePromise() {
  if (!pk) return null;
  if (!cachedStripePromise) cachedStripePromise = loadStripe(pk);
  return cachedStripePromise;
}

export default function PaymentMethodPage() {
  const [clientSecret, setClientSecret] = useState(null);
  const [hasDefault, setHasDefault] = useState(false);
  const [linking, setLinking] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [loadingStatus, setLoadingStatus] = useState(true);
  const [needLogin, setNeedLogin] = useState(false);

  // deposit UI
  const [amount, setAmount] = useState('25.00');
  const [depositing, setDepositing] = useState(false);
  const [wallet, setWallet] = useState(null); // {availableCents, pendingCents}

  // Required by Stripe (at least name)
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');

  const stripePromise = useMemo(getStripePromise, [pk]);

  /* ------ Check if user already has a default PM + load wallet ------ */
  useEffect(() => {
    const ac = new AbortController();

    // If token is missing/expired, show inline banner and stop
    const t = getToken();
    if (!t || jwtIsExpired(t)) {
      setNeedLogin(true);
      setLoadingStatus(false);
      return () => {};
    }

    (async () => {
      try {
        const r = await fetch('/api/payments/has-default-payment-method', {
          headers: { ...authHeaders() },
          signal: ac.signal,
        });
        const d = r.ok ? await r.json() : { hasDefault: false };
        setHasDefault(!!d.hasDefault);
      } catch {
        setHasDefault(false);
      } finally {
        setLoadingStatus(false);
      }

      try {
        const w = await fetchWallet();
        setWallet(w);
      } catch {
        // ignore
      }
    })();

    return () => ac.abort();
  }, []);

  async function refreshWallet() {
    try {
      const w = await fetchWallet();
      setWallet(w);
    } catch {
      // ignore
    }
  }

  async function startBankLink() {
    setLinking(true);
    setErrorMsg('');

    // quick local guard
    const t = getToken();
    if (!t || jwtIsExpired(t)) {
      setNeedLogin(true);
      setLinking(false);
      return;
    }

    try {
      const r = await fetch('/api/payments/create-setup-intent', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...authHeaders() },
      });

      if (r.status === 401) {
        // Keep user here and show a banner instead of redirecting away
        setNeedLogin(true);
        return;
      }
      if (!r.ok) throw new Error((await r.text()) || 'Failed to create SetupIntent.');

      const { clientSecret } = await r.json();
      if (!clientSecret) throw new Error('No client_secret returned from server.');
      setClientSecret(clientSecret);
    } catch (e) {
      setErrorMsg(e.message || 'Something went wrong starting bank link.');
    } finally {
      setLinking(false);
    }
  }

  const disabledLinkButton = linking || !pk || !stripePromise || !fullName;

  return (
    <div style={{ maxWidth: 560, margin: '0 auto', padding: 16 }}>
      <h2 style={{ marginTop: 0 }}>Payment Method</h2>

      {!pk && (
        <div
          style={{
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#7f1d1d',
            padding: '10px 12px',
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          Invalid or missing Stripe publishable key. In <code>p2p_contract_frontend/.env</code>{' '}
          set: <code>REACT_APP_STRIPE_PUBLISHABLE_KEY=pk_test_…</code> (no quotes), then restart{' '}
          <code>npm start</code>.
        </div>
      )}

      {needLogin && (
        <div
          style={{
            background: '#fef3c7',
            border: '1px solid #fcd34d',
            color: '#92400e',
            padding: '10px 12px',
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          Your session has expired. Please{' '}
          <a href={`/login?returnTo=${encodeURIComponent(location.pathname)}`}>log in</a> again to
          link your bank.
        </div>
      )}

      {errorMsg && (
        <div
          style={{
            background: '#fff7ed',
            border: '1px solid #fed7aa',
            color: '#7c2d12',
            padding: '10px 12px',
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          {errorMsg}
        </div>
      )}

      {wallet && (
        <div
          style={{
            background: '#f1f5f9',
            border: '1px solid #e2e8f0',
            padding: 10,
            borderRadius: 8,
            marginBottom: 12,
            fontSize: 14,
          }}
        >
          <strong>Wallet</strong> — Available:{' '}
          {`$${(((wallet.availableCents ?? 0) / 100)).toFixed(2)}`} • Pending:{' '}
          {`$${(((wallet.pendingCents ?? 0) / 100)).toFixed(2)}`}
        </div>
      )}

      {loadingStatus ? (
        <p>Checking your payment method…</p>
      ) : (
        <>
          <p>{hasDefault ? 'Bank account on file for ACH debits.' : 'No bank account on file.'}</p>

          {/* Link bank section (shown only when none on file) */}
          {!hasDefault && (
            <>
              <div style={{ display: 'grid', gap: 8, margin: '8px 0 12px' }}>
                <input
                  type="text"
                  value={fullName}
                  onChange={(e) => setFullName(e.target.value)}
                  placeholder="Account holder full name"
                  style={{ padding: '8px 10px', border: '1px solid #e2e8f0', borderRadius: 8 }}
                />
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Email (optional but recommended)"
                  style={{ padding: '8px 10px', border: '1px solid #e2e8f0', borderRadius: 8 }}
                />
              </div>

              <button
                onClick={startBankLink}
                disabled={disabledLinkButton || needLogin}
                style={{
                  background: '#4f46e5',
                  color: '#fff',
                  border: '1px solid #4f46e5',
                  padding: '8px 14px',
                  borderRadius: 8,
                  cursor: disabledLinkButton || needLogin ? 'not-allowed' : 'pointer',
                }}
              >
                {linking ? 'Starting…' : 'Link Bank Account'}
              </button>
            </>
          )}

          {/* Stripe bank link confirmation UI */}
          {clientSecret && stripePromise && (
            <Elements stripe={stripePromise}>
              <BankLinkFlow
                clientSecret={clientSecret}
                fullName={fullName}
                email={email}
                onComplete={async () => {
                  setHasDefault(true);
                  setClientSecret(null);
                  await refreshWallet();
                }}
                onError={setErrorMsg}
              />
            </Elements>
          )}

          {/* Deposit panel shows once a default method exists */}
          {hasDefault && (
            <div
              style={{
                marginTop: 20,
                padding: 12,
                borderRadius: 8,
                border: '1px solid #e2e8f0',
                background: '#ffffff',
              }}
            >
              <h3 style={{ margin: '0 0 8px' }}>Deposit to PeerFund Balance</h3>
              <p style={{ marginTop: 0, color: '#475569' }}>
                Funds will be debited from your linked bank and added to your available balance.
              </p>

              <DepositBox
                amount={amount}
                setAmount={setAmount}
                depositing={depositing}
                setDepositing={setDepositing}
                onSuccess={async () => {
                  await refreshWallet();
                  alert('Deposit initiated successfully.');
                }}
                onError={setErrorMsg}
              />
            </div>
          )}
        </>
      )}
    </div>
  );
}

/* ---------------- BankLinkFlow ---------------- */
function BankLinkFlow({ clientSecret, fullName, email, onComplete, onError }) {
  const stripe = useStripe();
  const [working, setWorking] = useState(false);

  async function handleLink() {
    if (!stripe) {
      onError?.('Stripe not ready yet.');
      return;
    }
    if (!fullName) {
      onError?.('Please enter the account holder’s full name.');
      return;
    }

    setWorking(true);
    try {
      // 1) Collect via Financial Connections
      const { error: collectError } = await stripe.collectBankAccountForSetup({
        clientSecret,
        params: {
          payment_method_type: 'us_bank_account',
          payment_method_data: {
            billing_details: {
              name: fullName,
              ...(email ? { email } : {}),
            },
          },
        },
      });
      if (collectError) throw collectError;

      // 2) Confirm the SetupIntent
      const { setupIntent, error: confirmError } =
        await stripe.confirmUsBankAccountSetup(clientSecret);
      if (confirmError) throw confirmError;

      // 3) React to status
      switch (setupIntent.status) {
        case 'succeeded':
          onComplete?.();
          alert('Bank account saved. ACH payments are now enabled.');
          break;

        case 'requires_action': {
          const na = setupIntent.next_action;
          if (na?.type === 'verify_with_microdeposits') {
            alert(
              'Bank linked. Stripe is sending two small micro-deposits to your account. ' +
                'ACH will enable after verification (1–2 business days).'
            );
          } else {
            alert('Bank linked; additional verification required.');
          }
          onComplete?.();
          break;
        }

        default:
          alert(`Setup in progress (status: ${setupIntent.status}).`);
          onComplete?.();
      }
    } catch (e) {
      onError?.(e?.message || 'Failed to link bank account.');
    } finally {
      setWorking(false);
    }
  }

  return (
    <div style={{ marginTop: 12 }}>
      <button
        onClick={handleLink}
        disabled={!stripe || working}
        style={{
          background: '#10b981',
          color: '#fff',
          border: '1px solid #10b981',
          padding: '8px 14px',
          borderRadius: 8,
          cursor: !stripe || working ? 'not-allowed' : 'pointer',
        }}
      >
        {working ? 'Connecting…' : 'Connect and Save Bank'}
      </button>
    </div>
  );
}

BankLinkFlow.propTypes = {
  clientSecret: PropTypes.string.isRequired,
  fullName: PropTypes.string.isRequired,
  email: PropTypes.string,
  onComplete: PropTypes.func,
  onError: PropTypes.func,
};

/* ---------------- DepositBox ---------------- */
function DepositBox({ amount, setAmount, depositing, setDepositing, onSuccess, onError }) {
  const stripe = useStripe();

  async function handleDeposit() {
    const n = Number(amount);
    if (!Number.isFinite(n) || n <= 0) {
      onError?.('Enter a valid deposit amount.');
      return;
    }
    setDepositing(true);
    onError?.('');

    try {
      // Ask backend to create a PaymentIntent against the customer’s default PM
      const { clientSecret, ledgerId, simulated } = await createDepositIntent(n);

      // In dev mode the backend may simulate the deposit
      if (simulated) {
        await devConfirmDeposit(ledgerId);
        onSuccess?.();
        return;
      }

      if (!stripe) throw new Error('Stripe not ready.');
      const { paymentIntent, error } = await stripe.confirmUsBankAccountPayment(clientSecret);
      if (error) throw error;

      const status = paymentIntent?.status;

      if (status === 'succeeded') {
        onSuccess?.();
      } else if (status === 'processing') {
        onSuccess?.();
      } else if (status === 'requires_action') {
        const na = paymentIntent.next_action;
        if (na?.type === 'verify_with_microdeposits') {
          onError?.('');
          alert(
            'Deposit initiated. ACH requires micro-deposit verification; ' +
              'funds will appear after verification.'
          );
          onSuccess?.();
        } else {
          onError?.('Additional action is required to complete this deposit.');
        }
      } else {
        onError?.(`Deposit status: ${status || 'unknown'}.`);
      }
    } catch (e) {
      onError?.(e?.message || 'Deposit failed.');
    } finally {
      setDepositing(false);
    }
  }

  return (
    <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 6 }}>
        <span>$</span>
        <input
          type="number"
          step="0.01"
          min="1"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          style={{
            width: 120,
            padding: '8px 10px',
            border: '1px solid #e2e8f0',
            borderRadius: 8,
          }}
        />
      </div>
      <button
        onClick={handleDeposit}
        disabled={depositing}
        style={{
          background: '#4f46e5',
          color: '#fff',
          border: '1px solid #4f46e5',
          padding: '8px 14px',
          borderRadius: 8,
          cursor: depositing ? 'not-allowed' : 'pointer',
        }}
      >
        {depositing ? 'Processing…' : 'Deposit'}
      </button>
    </div>
  );
}

DepositBox.propTypes = {
  amount: PropTypes.string.isRequired,
  setAmount: PropTypes.func.isRequired,
  depositing: PropTypes.bool.isRequired,
  setDepositing: PropTypes.bool,
  onSuccess: PropTypes.func,
  onError: PropTypes.func,
};
