// src/features/profile/LendingTermsEditor.jsx
import React, { useEffect, useMemo, useState } from 'react';

function clampRate(v) {
  const n = Number(v);
  if (!Number.isFinite(n)) return 0;
  if (n < 0) return 0;
  if (n > 100) return 100;
  return Math.round(n * 100) / 100;
}
function clampAmount(v) {
  const n = Number(v);
  if (!Number.isFinite(n) || n <= 0) return 0;
  // allow cents? If you want whole dollars, use Math.round(n)
  return Math.round(n * 100) / 100;
}
function uid() {
  return Math.random().toString(36).slice(2, 9);
}

export default function LendingTermsEditor() {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [err, setErr] = useState('');
  const [ok, setOk] = useState('');

  // Editable rows instead of fixed presets
  // { id, amount, enabled, rate }
  const [rows, setRows] = useState([]);

  // Load existing terms -> rows
  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        setLoading(true);
        setErr('');
        const r = await fetch('/api/users/me/lending-terms', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        if (!r.ok) throw new Error(await r.text());
        const d = await r.json();
        const map = d?.lendingTerms || {};
        const parsed = Object.entries(map).map(([k, v]) => ({
          id: uid(),
          amount: clampAmount(k),
          enabled: !!v?.enabled,
          rate: clampRate(v?.rate),
        }));
        // Sort by amount asc
        parsed.sort((a, b) => a.amount - b.amount);
        if (alive) setRows(parsed);
      } catch (e) {
        console.error(e);
        if (alive) setErr('Failed to load lending terms.');
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, []);

  // Derived validation
  const dupAmounts = useMemo(() => {
    const seen = new Map();
    for (const r of rows) {
      const key = String(r.amount);
      seen.set(key, (seen.get(key) || 0) + 1);
    }
    return new Set([...seen.entries()].filter(([, c]) => c > 1).map(([k]) => k));
  }, [rows]);

  function addRow() {
    setRows((rs) => [
      ...rs,
      { id: uid(), amount: 50, enabled: true, rate: 10 },
    ]);
  }
  function removeRow(id) {
    setRows((rs) => rs.filter((r) => r.id !== id));
  }
  function updateRow(id, patch) {
    setRows((rs) => rs.map((r) => (r.id === id ? { ...r, ...patch } : r)));
  }

  async function save() {
    // client-side validation
    const badAmount = rows.find((r) => !Number.isFinite(r.amount) || r.amount <= 0);
    if (badAmount) {
      setErr('Amounts must be positive numbers.');
      setOk('');
      return;
    }
    if (dupAmounts.size > 0) {
      setErr('Duplicate amounts detected. Each tier must be unique.');
      setOk('');
      return;
    }

    // Convert rows -> map keyed by amount string
    const map = Object.fromEntries(
      rows
        .slice()
        .sort((a, b) => a.amount - b.amount)
        .map((r) => [
          String(r.amount), // keep as string keys like before
          { enabled: !!r.enabled, rate: clampRate(r.rate) },
        ])
    );

    try {
      setSaving(true);
      setErr('');
      setOk('');
      const r = await fetch('/api/users/me/lending-terms', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ lendingTerms: map }),
      });
      if (!r.ok) throw new Error(await r.text());
      setOk('Lending terms saved.');
    } catch (e) {
      console.error(e);
      setErr('Failed to save lending terms.');
    } finally {
      setSaving(false);
    }
  }

  if (loading) return <div className="lt-card">Loading lending terms…</div>;

  return (
    <div className="lt-card">
      <div className="lt-title">Lending Terms</div>
      <p className="lt-sub">
        Add the amounts you’re willing to fund and the APR you’ll charge.
      </p>

      {err && <div className="lt-alert lt-alert--err">{err}</div>}
      {ok && <div className="lt-alert lt-alert--ok">{ok}</div>}

      <div className="lt-grid">
        {rows.length === 0 && (
          <div className="ms-muted" style={{ marginBottom: 8 }}>
            No tiers yet. Add one below.
          </div>
        )}

        {rows.map((r) => {
          const isDup = dupAmounts.has(String(r.amount));
          return (
            <div className="lt-row" key={r.id}>
              <label className="lt-toggle" title="Enable/disable this tier">
                <input
                  type="checkbox"
                  checked={!!r.enabled}
                  onChange={(e) => updateRow(r.id, { enabled: e.target.checked })}
                />
                <span>&nbsp;</span>
              </label>

              {/* Amount input with $ prefix */}
              <div className="lt-amount">
                <span className="lt-prefix">$</span>
                <input
                  type="number"
                  min="1"
                  step="1"
                  value={Number.isFinite(r.amount) ? r.amount : ''}
                  onChange={(e) => updateRow(r.id, { amount: clampAmount(e.target.value) })}
                  className={isDup ? 'has-error' : ''}
                  title={isDup ? 'Duplicate amount' : 'Amount in dollars'}
                />
              </div>

              {/* Rate */}
              <div className="lt-rate">
                <input
                  type="number"
                  min="0"
                  max="100"
                  step="0.25"
                  value={r.rate}
                  onChange={(e) => updateRow(r.id, { rate: clampRate(e.target.value) })}
                  disabled={!r.enabled}
                />
                <span className="lt-rate-suffix">% APR</span>
              </div>

              <button
                type="button"
                className="action-btn xs"
                onClick={() => removeRow(r.id)}
                title="Remove tier"
              >
                ✕
              </button>
            </div>
          );
        })}
      </div>

      <div className="lt-actions" style={{ gap: 8 }}>
        <button type="button" className="action-btn" onClick={addRow}>
          + Add Tier
        </button>
        <button className="action-btn primary" onClick={save} disabled={saving}>
          {saving ? 'Saving…' : 'Save Terms'}
        </button>
      </div>
    </div>
  );
}
