// src/features/dashboard/TransactionHistory.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { CSVLink } from 'react-csv';

const TransactionHistory = () => {
  const [transactions, setTransactions] = useState([]);
  const [totals, setTotals] = useState({ sent: 0, received: 0, net: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        setLoading(true);
        setError('');

        const res = await axios.get('/api/transactions', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });

        const data = Array.isArray(res.data) ? res.data : [];
        setTransactions(data);

        // Summary totals (using safe numeric conversion)
        let sent = 0;
        let received = 0;

        data.forEach((tx) => {
          const amt = Number(tx.amount || 0);

          if (tx.type === 'REPAYMENT') {
            received += amt;
          } else if (tx.type === 'DISBURSEMENT') {
            sent += amt;
          }
        });

        setTotals({
          sent,
          received,
          net: received - sent,
        });
      } catch (err) {
        console.error('Failed to fetch transactions:', err);
        setError('Failed to fetch transactions. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  const headers = [
    { label: 'Date', key: 'timestamp' },
    { label: 'Type', key: 'type' },
    { label: 'Amount', key: 'amount' },
    { label: 'From', key: 'fromUser.name' },
    { label: 'To', key: 'toUser.name' },
  ];

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Transaction History</h2>

      {error && (
        <div
          style={{
            marginTop: 8,
            marginBottom: 12,
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#7f1d1d',
            borderRadius: 8,
            padding: '8px 10px',
          }}
        >
          {error}
        </div>
      )}

      <div style={{ marginBottom: '1rem' }}>
        <p>
          <strong>Total Sent:</strong> ${Number(totals.sent).toFixed(2)}
        </p>
        <p>
          <strong>Total Received:</strong> ${Number(totals.received).toFixed(2)}
        </p>
        <p>
          <strong>Net:</strong> ${Number(totals.net).toFixed(2)}
        </p>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <CSVLink
          data={transactions}
          headers={headers}
          filename="transactions.csv"
        >
          <button>⬇️ Download CSV</button>
        </CSVLink>
      </div>

      {loading ? (
        <p>Loading transactions…</p>
      ) : (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            maxWidth: '800px',
          }}
        >
          <thead>
            <tr>
              <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd', padding: '8px' }}>Date</th>
              <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd', padding: '8px' }}>Type</th>
              <th style={{ textAlign: 'right', borderBottom: '1px solid #ddd', padding: '8px' }}>Amount</th>
              <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd', padding: '8px' }}>From</th>
              <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd', padding: '8px' }}>To</th>
            </tr>
          </thead>
          <tbody>
            {transactions.length === 0 ? (
              <tr>
                <td colSpan={5} style={{ padding: '12px', textAlign: 'center' }}>
                  No transactions yet.
                </td>
              </tr>
            ) : (
              transactions.map((tx) => (
                <tr key={tx.id}>
                  <td style={{ padding: '8px' }}>
                    {tx.timestamp
                      ? new Date(tx.timestamp).toLocaleString()
                      : '-'}
                  </td>
                  <td style={{ padding: '8px' }}>{tx.type}</td>
                  <td style={{ padding: '8px', textAlign: 'right' }}>
                    ${Number(tx.amount || 0).toFixed(2)}
                  </td>
                  <td style={{ padding: '8px' }}>{tx.fromUser?.name || '-'}</td>
                  <td style={{ padding: '8px' }}>{tx.toUser?.name || '-'}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default TransactionHistory;
