// src/features/wallet/WalletPage.jsx
import React, { useEffect, useState } from 'react';
import WalletPanel from './WalletPanel';
import { fetchWallet } from './walletApi';
import SaveFundingCard from './SaveFundingCard';

/**
 * Simple withdraw modal.
 * Assumes backend route: POST /api/wallet/withdraw
 * Body: { amountDollars }
 */
function WithdrawPanel({ onClose, onBalanceUpdated, maxDollars }) {
  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState('');

  const raw = (amount || '').replace(/[^0-9.]/g, '');
  const numeric = Number(raw || 0);
  const clamped = Number.isFinite(numeric) && numeric > 0 ? numeric : 0;

  const exceedsBalance =
    typeof maxDollars === 'number' && clamped > maxDollars + 1e-6;

  const canSubmit = !busy && clamped > 0 && !exceedsBalance;

  async function withdraw() {
    if (!canSubmit) return;

    try {
      setBusy(true);
      setError('');

      const res = await fetch('/api/wallet/withdraw', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ amountDollars: clamped }),
      });

      if (!res.ok) {
        let msg = 'Failed to withdraw funds';
        try {
          const data = await res.json();
          if (data?.error) msg = data.error;
        } catch {
          // ignore JSON parse errors
        }
        throw new Error(msg);
      }

      // Let parent refresh wallet & close modal
      await onBalanceUpdated?.();
    } catch (e) {
      console.error('Withdraw failed:', e);
      setError(e.message || 'Failed to withdraw funds');
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="withdraw-panel">
      <h3>Withdraw funds</h3>

      {typeof maxDollars === 'number' && (
        <p style={{ fontSize: 13, color: '#6b7280', marginTop: 4 }}>
          Available to withdraw: <strong>${maxDollars.toFixed(2)}</strong>
        </p>
      )}

      {error && (
        <div
          style={{
            marginTop: 8,
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#7f1d1d',
            borderRadius: 8,
            padding: '8px 10px',
          }}
        >
          {error}
        </div>
      )}

      <div className="row" style={{ marginTop: 12 }}>
        <label>Amount</label>
        <div className="input-wrap" style={{ display: 'flex', gap: 4 }}>
          <span
            style={{
              padding: '6px 10px',
              borderRadius: 8,
              border: '1px solid #e2e8f0',
              background: '#f9fafb',
              fontSize: 14,
            }}
          >
            $
          </span>
          <input
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
            style={{
              flex: 1,
              padding: '6px 10px',
              borderRadius: 8,
              border: '1px solid #e2e8f0',
            }}
          />
        </div>
        {exceedsBalance && (
          <div
            style={{
              marginTop: 6,
              fontSize: 12,
              color: '#b91c1c',
            }}
          >
            Amount exceeds your available balance.
          </div>
        )}
      </div>

      <div className="row right" style={{ marginTop: 16, textAlign: 'right' }}>
        <button
          className="btn"
          onClick={onClose}
          disabled={busy}
          style={{ marginRight: 8 }}
        >
          Cancel
        </button>
        <button
          className="btn primary"
          onClick={withdraw}
          disabled={!canSubmit}
          style={{
            padding: '8px 14px',
            borderRadius: 999,
            border: '1px solid #4f46e5',
            background: canSubmit ? '#4f46e5' : '#9ca3af',
            color: '#fff',
            fontWeight: 700,
            cursor: canSubmit ? 'pointer' : 'not-allowed',
          }}
        >
          {busy ? 'Processing…' : 'Withdraw'}
        </button>
      </div>
    </div>
  );
}

export default function WalletPage() {
  const [wallet, setWallet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showDepositPanel, setShowDepositPanel] = useState(false);
  const [showWithdrawPanel, setShowWithdrawPanel] = useState(false);
  const [error, setError] = useState('');

  async function loadWallet() {
    try {
      setError('');
      const w = await fetchWallet();
      setWallet(w);
    } catch (e) {
      console.error('loadWallet error', e);
      setError(e?.message || 'Failed to load wallet.');
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    loadWallet();
  }, []);

  const availableCents = wallet?.availableCents ?? 0;
  const pendingCents = wallet?.pendingCents ?? 0;
  const availableFloat = availableCents / 100;
  const availableDollars = availableFloat.toFixed(2);
  const pendingDollars = (pendingCents / 100).toFixed(2);

  const anyModalOpen = showDepositPanel || showWithdrawPanel;

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Wallet</h2>

      {error && (
        <div
          style={{
            marginTop: 8,
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#7f1d1d',
            borderRadius: 8,
            padding: '8px 10px',
          }}
        >
          {error}
        </div>
      )}

      {loading ? (
        <p>Loading wallet…</p>
      ) : (
        <>
          {/* Balance card */}
          <div
            style={{
              marginTop: 12,
              padding: '12px 14px',
              borderRadius: 12,
              border: '1px solid #e2e8f0',
              background: '#f8fafc',
              maxWidth: 420,
            }}
          >
            <div style={{ fontSize: 14, color: '#64748b', marginBottom: 4 }}>
              PeerFund balance
            </div>
            <div style={{ fontSize: 24, fontWeight: 700 }}>
              ${availableDollars}
            </div>
            <div style={{ fontSize: 13, color: '#6b7280', marginTop: 4 }}>
              Pending: ${pendingDollars}
            </div>

            <div style={{ marginTop: 12, display: 'flex', gap: 8 }}>
              <button
                style={{
                  flex: 1,
                  padding: '8px 14px',
                  borderRadius: 999,
                  border: '1px solid #4f46e5',
                  background: '#4f46e5',
                  color: '#fff',
                  fontWeight: 700,
                  cursor: 'pointer',
                }}
                onClick={() => setShowDepositPanel(true)}
              >
                Add funds
              </button>
              <button
                style={{
                  flex: 1,
                  padding: '8px 14px',
                  borderRadius: 999,
                  border: '1px solid #0f172a',
                  background: '#0f172a',
                  color: '#fff',
                  fontWeight: 700,
                  cursor:
                    availableFloat > 0 ? 'pointer' : 'not-allowed',
                  opacity: availableFloat > 0 ? 1 : 0.6,
                }}
                disabled={availableFloat <= 0}
                onClick={() => setShowWithdrawPanel(true)}
              >
                Withdraw
              </button>
            </div>
          </div>

          {/* Funding card section – only show when no modal is open */}
          {!anyModalOpen && (
            <div
              style={{
                marginTop: 20,
                padding: '12px 14px',
                borderRadius: 12,
                border: '1px solid #e2e8f0',
                background: '#ffffff',
                maxWidth: 420,
              }}
            >
              <div style={{ fontSize: 14, color: '#64748b', marginBottom: 8 }}>
                Funding card
              </div>
              <p style={{ fontSize: 13, color: '#6b7280', marginBottom: 12 }}>
                This card is used when you deposit to your PeerFund wallet,
                repay loans, or pay the SuperUser fee.
              </p>

              <SaveFundingCard />
            </div>
          )}

          {/* Add-funds modal */}
          {showDepositPanel && (
            <div
              style={{
                position: 'fixed',
                inset: 0,
                background: 'rgba(15,23,42,0.45)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 50,
              }}
            >
              <div
                style={{
                  background: '#fff',
                  borderRadius: 16,
                  padding: 20,
                  minWidth: 320,
                  maxWidth: 420,
                  boxShadow: '0 20px 40px rgba(15,23,42,0.25)',
                }}
              >
                <WalletPanel
                  onClose={() => setShowDepositPanel(false)}
                  onBalanceUpdated={async () => {
                    setShowDepositPanel(false);
                    await loadWallet();
                  }}
                />
              </div>
            </div>
          )}

          {/* Withdraw modal */}
          {showWithdrawPanel && (
            <div
              style={{
                position: 'fixed',
                inset: 0,
                background: 'rgba(15,23,42,0.45)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 50,
              }}
            >
              <div
                style={{
                  background: '#fff',
                  borderRadius: 16,
                  padding: 20,
                  minWidth: 320,
                  maxWidth: 420,
                  boxShadow: '0 20px 40px rgba(15,23,42,0.25)',
                }}
              >
                <WithdrawPanel
                  maxDollars={availableFloat}
                  onClose={() => setShowWithdrawPanel(false)}
                  onBalanceUpdated={async () => {
                    setShowWithdrawPanel(false);
                    await loadWallet();
                  }}
                />
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}
