// Sidebar.jsx
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  const navigate = useNavigate();
  const { pathname } = useLocation();

  const items = [
    { label: 'Dashboard', to: '/dashboard' },
    { label: 'Request Loan', to: '/create-loan' },
    { label: 'My Money Summary', to: '/money-summary' },
    { label: 'View Open Loans', to: '/loan-marketplace' },
    { label: 'Transaction History', to: '/history' },
    { label: 'Payment Method', to: '/payment-method' },
    { label: 'Edit Profile', to: '/profile' },
  ];

  const closeDrawer = () => {
    document.body.setAttribute('data-sidebar', 'closed');
  };

  return (
    <>
      {/* Backdrop */}
      <div
        className="sidebar__backdrop"
        onClick={closeDrawer}
        aria-hidden="true"
      />

      {/* Drawer / left column */}
      <aside className="sidebar">
        <div className="sidebar__brand">Bank Logo</div>
        <ul className="sidebar__nav">
          {items.map(item => (
            <li key={item.to}>
              <button
                className={`sidebar__btn ${pathname === item.to ? 'is-active' : ''}`}
                onClick={() => {
                  navigate(item.to);
                  closeDrawer(); // close after selection on small screens
                }}
              >
                {item.label}
              </button>
            </li>
          ))}
        </ul>
      </aside>
    </>
  );
};

export default Sidebar;
