// src/components/Sidebar.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  const navigate = useNavigate();
  const { pathname } = useLocation();

  const items = [
    { label: 'Dashboard', to: '/dashboard' },
    { label: 'Request Loan', to: '/create-loan' },
    { label: 'My Money Summary', to: '/money-summary' },
    { label: 'View Open Loans', to: '/loan-marketplace' },
    { label: 'Transaction History', to: '/history' },
    { label: 'Payment Method', to: '/payment-method' },
    { label: 'Edit Profile', to: '/profile' },
  ];

  // Drawer state only matters on small screens; on desktop we just keep it open
  const prefersDesktop =
    typeof window !== 'undefined' &&
    window.matchMedia &&
    window.matchMedia('(min-width: 821px)').matches;

  const [open, setOpen] = useState(() => {
    const saved = localStorage.getItem('pf_sidebar_open');
    // On desktop, default "open"; on mobile default "closed" unless user saved
    if (saved === '1') return true;
    if (saved === '0') return false;
    return prefersDesktop ? true : false;
  });

  useEffect(() => {
    localStorage.setItem('pf_sidebar_open', open ? '1' : '0');
  }, [open]);

  // Close on Escape
  useEffect(() => {
    const onKey = (e) => {
      if (e.key === 'Escape') setOpen(false);
    };
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, []);

  return (
    <>
      {/* Hamburger (visible on small screens) */}
      <button
        className="sidebar__toggle"
        aria-label="Toggle navigation"
        aria-controls="app-sidenav"
        aria-expanded={open}
        onClick={() => setOpen((v) => !v)}
      >
        <svg width="22" height="22" viewBox="0 0 24 24" fill="none" aria-hidden="true">
          <path d="M3 6h18M3 12h18M3 18h18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
        </svg>
      </button>

      {/* Backdrop for mobile */}
      <div
        className={`sidebar__backdrop ${open ? 'is-open' : ''}`}
        onClick={() => setOpen(false)}
        aria-hidden={!open}
      />

      <aside
        id="app-sidenav"
        className={`sidebar ${open ? 'is-open' : ''}`}
        aria-hidden={!open && !prefersDesktop}
      >
        <div className="sidebar__brand">Bank Logo</div>
        <ul className="sidebar__nav">
          {items.map((item) => (
            <li key={item.to}>
              <button
                className={`sidebar__btn ${pathname === item.to ? 'is-active' : ''}`}
                onClick={() => {
                  navigate(item.to);
                  // On small screens, close after navigating
                  if (!prefersDesktop) setOpen(false);
                }}
              >
                {item.label}
              </button>
            </li>
          ))}
        </ul>
      </aside>
    </>
  );
};

export default Sidebar;
