// src/features/wallet/WalletPanel.jsx
import React, { useState } from 'react';
import { useStripe } from '@stripe/react-stripe-js';
import { createDepositIntent, fetchWallet } from './walletApi';

export default function WalletPanel({ onClose, onBalanceUpdated }) {
  const stripe = useStripe();
  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);

  // Parse dollars from the input
  const numericDollars =
    Number((amount || '').replace(/[^0-9.]/g, '')) || 0;

  const deposit = async () => {
    try {
      setBusy(true);

      if (!numericDollars || numericDollars <= 0) {
        alert('Please enter an amount greater than 0.');
        return;
      }

      // Call backend: will accept amountDollars and convert to cents
      const { clientSecret, simulated } = await createDepositIntent({
        amountDollars: numericDollars,
      });

      // If Stripe is configured (non-simulated), confirm the payment
      if (!simulated) {
        if (!stripe) {
          throw new Error('Stripe is not ready yet, please try again.');
        }

        const result = await stripe.confirmCardPayment(clientSecret);
        if (result.error) {
          throw new Error(result.error.message);
        }
      }

      // Refresh wallet balance
      const w = await fetchWallet();
      onBalanceUpdated?.(w.availableCents);
      onClose?.();
    } catch (e) {
      console.error('Deposit failed:', e);
      alert(e.message || 'Deposit failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="wallet-panel">
      <h3>Add funds</h3>
      <div className="row">
        <label>Amount</label>
        <div className="input-wrap">
          <span>$</span>
          <input
            value={amount}
            onChange={e => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
          />
        </div>
      </div>
      <div className="row right">
        <button className="btn" onClick={onClose}>Cancel</button>
        <button
          className="btn primary"
          onClick={deposit}
          disabled={busy}
        >
          {busy ? 'Processing…' : 'Continue'}
        </button>
      </div>
    </div>
  );
}
