// components/UserProfileCard.jsx
import React from 'react';
import PropTypes from 'prop-types';
import './UserProfileCard.css';

const UserProfileCard = ({ user, onRequestLoan, onMessage, hideActions = false }) => {
  const given =
    user?.stats?.loansGivenCount ??
    (Array.isArray(user?.loansAsLender) ? user.loansAsLender.length : 0);

  const received =
    user?.stats?.loansReceivedCount ??
    (Array.isArray(user?.loansAsBorrower) ? user.loansAsBorrower.length : 0);

  const initials =
    (user?.name || '')
      .split(' ')
      .map(p => p[0])
      .filter(Boolean)
      .join('')
      .slice(0, 2)
      .toUpperCase() || 'U';

  return (
    <div className="user-card">
      <div className="user-card__header">
        <div className="user-card__avatar">{initials}</div>
        <div className="user-card__meta">
          <h2 className="user-card__name">
            {user?.name || 'Unknown'}
            {user?.isSuperUser && <span className="user-card__badge">SuperUser</span>}
          </h2>
          <div className="user-card__sub">
            {user?.location ? <span>{user.location} • </span> : null}
            Member since:{' '}
            {user?.signupDate ? new Date(user.signupDate).toLocaleDateString() : '—'}
            <div className="user-card__stat-label">Loans Given</div>
          <div className="user-card__stat-value">{given}</div>
          </div>
        </div>
      </div>

      {!hideActions && (
        <div className="user-card__actions">
          {onMessage && (
            <button className="action-btn" onClick={onMessage}>Message</button>
          )}
          {onRequestLoan && (
            <button className="action-btn primary" onClick={onRequestLoan}>
              Request Loan
            </button>
          )}
        </div>
      )}
    </div>
  );
};

UserProfileCard.propTypes = {
  user: PropTypes.object.isRequired,
  onRequestLoan: PropTypes.func,
  onMessage: PropTypes.func,
  hideActions: PropTypes.bool,
};

export default UserProfileCard;
