// PaymentMethod.jsx (CRA) — ACH via Financial Connections, JWT auth header

import { useEffect, useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, useStripe } from '@stripe/react-stripe-js';

/* ---------- Env (CRA) ---------- */
const pk = process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY;

/* ---------- Bearer token helper ---------- */
const authHeaders = () => {
  const t = localStorage.getItem('token'); // adjust key if different
  return t ? { Authorization: `Bearer ${t}` } : {};
};

/* ---------- Safe Stripe init (cached) ---------- */
let cachedStripePromise = null;
function getStripePromise() {
  if (!pk) return null;
  if (!cachedStripePromise) cachedStripePromise = loadStripe(pk);
  return cachedStripePromise;
}

export default function PaymentMethodPage() {
  const [clientSecret, setClientSecret] = useState(null);
  const [hasDefault, setHasDefault] = useState(false);
  const [linking, setLinking] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [loadingStatus, setLoadingStatus] = useState(true);

  const stripePromise = useMemo(getStripePromise, [pk]);

  // Check whether a default PM exists
  useEffect(() => {
    const ac = new AbortController();
    (async () => {
      try {
        const r = await fetch('/api/payments/has-default-payment-method', {
          credentials: 'include',
          headers: { ...authHeaders() },
          signal: ac.signal,
        });
        if (!r.ok) throw new Error(`HTTP ${r.status}`);
        const d = await r.json();
        setHasDefault(!!d.hasDefault);
      } catch (e) {
        setHasDefault(false);
      } finally {
        setLoadingStatus(false);
      }
    })();
    return () => ac.abort();
  }, []);

  async function startBankLink() {
    setLinking(true);
    setErrorMsg('');
    try {
      const r = await fetch('/api/payments/create-setup-intent', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
          ...authHeaders(),
        },
      });

      if (r.status === 401) {
        throw new Error('Please log in again to link your bank.');
      }
      if (!r.ok) throw new Error((await r.text()) || 'Failed to create SetupIntent.');

      const { clientSecret } = await r.json();
      if (!clientSecret) throw new Error('No client_secret returned from server.');
      setClientSecret(clientSecret);
    } catch (e) {
      setErrorMsg(e.message || 'Something went wrong starting bank link.');
    } finally {
      setLinking(false);
    }
  }

  return (
    <div style={{ maxWidth: 560, margin: '0 auto', padding: 16 }}>
      <h2 style={{ marginTop: 0 }}>Payment Method</h2>

      {!pk && (
        <div
          style={{
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#7f1d1d',
            padding: '10px 12px',
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          Stripe publishable key missing. Add <code>REACT_APP_STRIPE_PUBLISHABLE_KEY</code> in your
          frontend <code>.env</code> (no quotes), then restart <code>npm start</code>.
        </div>
      )}

      {errorMsg && (
        <div
          style={{
            background: '#fff7ed',
            border: '1px solid #fed7aa',
            color: '#7c2d12',
            padding: '10px 12px',
            borderRadius: 8,
            marginBottom: 12,
          }}
        >
          {errorMsg}
        </div>
      )}

      {loadingStatus ? (
        <p>Checking your payment method…</p>
      ) : (
        <>
          <p>{hasDefault ? 'Bank account on file for ACH debits.' : 'No bank account on file.'}</p>

          {!hasDefault && (
            <button
              onClick={startBankLink}
              disabled={linking || !pk || !stripePromise}
              style={{
                background: '#4f46e5',
                color: '#fff',
                border: '1px solid #4f46e5',
                padding: '8px 14px',
                borderRadius: 8,
                cursor: linking || !pk || !stripePromise ? 'not-allowed' : 'pointer',
              }}
            >
              {linking ? 'Starting…' : 'Link Bank Account'}
            </button>
          )}

          {/* Show Financial Connections only AFTER we have a clientSecret */}
          {clientSecret && stripePromise && (
            <Elements stripe={stripePromise}>
              <BankLinkFlow
                clientSecret={clientSecret}
                onComplete={() => {
                  setHasDefault(true);
                  setClientSecret(null);
                }}
                onError={setErrorMsg}
              />
            </Elements>
          )}
        </>
      )}
    </div>
  );
}

/* --- BankLinkFlow --- */
function BankLinkFlow({ clientSecret, onComplete, onError }) {
  const stripe = useStripe();
  const [working, setWorking] = useState(false);

  async function handleLink() {
    if (!stripe) {
      onError?.('Stripe not ready yet. Please try again.');
      return;
    }
    setWorking(true);
    try {
      // 1) Open Financial Connections modal to collect a US bank account
      const { error: collectError } = await stripe.collectBankAccountForSetup({
        clientSecret,
        params: { payment_method_type: 'us_bank_account' },
      });
      if (collectError) throw collectError;

      // 2) Confirm the SetupIntent to attach the PM to the customer
      const { error: confirmError } = await stripe.confirmUsBankAccountSetup(clientSecret);
      if (confirmError) throw confirmError;

      onComplete?.();
      alert('Bank account saved. ACH payments are now enabled.');
    } catch (e) {
      onError?.(e?.message || 'Failed to link bank account.');
    } finally {
      setWorking(false);
    }
  }

  return (
    <div style={{ marginTop: 12 }}>
      <button
        onClick={handleLink}
        disabled={!stripe || working}
        style={{
          background: '#10b981',
          color: '#fff',
          border: '1px solid #10b981',
          padding: '8px 14px',
          borderRadius: 8,
          cursor: !stripe || working ? 'not-allowed' : 'pointer',
        }}
      >
        {working ? 'Connecting…' : 'Connect and Save Bank'}
      </button>
    </div>
  );
}

BankLinkFlow.propTypes = {
  clientSecret: PropTypes.string.isRequired,
  onComplete: PropTypes.func,
  onError: PropTypes.func,
};
