// src/features/wallet/WalletPanel.jsx
import React, { useState } from 'react';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { createDepositIntent, fetchWallet, devConfirmDeposit } from './walletApi';

export default function WalletPanel({ onClose, onBalanceUpdated }) {
  const stripe = useStripe();
  const elements = useElements();

  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);

  // Normalize amount -> dollars number
  const amountDollars = (() => {
    const clean = String(amount).replace(/[^0-9.]/g, '');
    const n = Number(clean);
    return Number.isFinite(n) && n > 0 ? n : 0;
  })();

  async function deposit() {
    try {
      if (!amountDollars || amountDollars <= 0) {
        alert('Enter a valid amount.');
        return;
      }

      setBusy(true);

      // Ask backend to create a PaymentIntent + ledger row
      const { clientSecret, simulated, ledgerId } = await createDepositIntent({
        amountDollars,
      });

      if (simulated) {
        // Dev-only branch (no real Stripe charge) – confirm on backend
        await devConfirmDeposit({ ledgerId });
      } else {
        if (!stripe || !elements) {
          alert('Stripe is still loading. Please wait a moment and try again.');
          return;
        }

        const cardElement = elements.getElement(CardElement);
        if (!cardElement) {
          alert('Card input is missing.');
          return;
        }

        // Attach card to the PaymentIntent and confirm
        const result = await stripe.confirmCardPayment(clientSecret, {
          payment_method: {
            card: cardElement,
          },
        });

        if (result.error) {
          throw new Error(result.error.message);
        }
      }

      // Refresh wallet balance after successful deposit
      const wallet = await fetchWallet();
      if (onBalanceUpdated) onBalanceUpdated(wallet.availableCents);
      if (onClose) onClose();
    } catch (e) {
      console.error('Deposit failed:', e);
      alert(`Deposit failed: ${e.message || 'Unknown error'}`);
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="wallet-panel">
      <h3>Add funds</h3>

      <div className="row">
        <label>Amount</label>
        <div className="input-wrap">
          <span>$</span>
          <input
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
          />
        </div>
      </div>

      <div className="row" style={{ marginTop: 12 }}>
        <label>Card details</label>
        <div
          style={{
            border: '1px solid #e2e8f0',
            borderRadius: 8,
            padding: '8px 10px',
            background: '#fff',
          }}
        >
          <CardElement
            options={{
              style: {
                base: {
                  fontSize: '14px',
                  color: '#0f172a',
                  '::placeholder': { color: '#94a3b8' },
                },
              },
            }}
          />
        </div>
      </div>

      <div className="row right" style={{ marginTop: 16 }}>
        <button className="btn" onClick={onClose} disabled={busy}>
          Cancel
        </button>
        <button className="btn primary" onClick={deposit} disabled={busy || !amountDollars}>
          {busy ? 'Processing…' : 'Continue'}
        </button>
      </div>
    </div>
  );
}
