// Navbar.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import NotificationBell from './NotificationBell';
import './Navbar.css';
import './Sidebar.css';

const Navbar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // Toggle a body attribute so Sidebar.css can open/close the drawer
  const toggleSidebar = () => {
    const cur = document.body.getAttribute('data-sidebar');
    document.body.setAttribute('data-sidebar', cur === 'open' ? 'closed' : 'open');
  };

  return (
    <nav className="navbar">
      {/* Left: hamburger */}
      <button
        className="navbar__toggle"
        aria-label="Toggle sidebar"
        onClick={toggleSidebar}
      >
        {/* simple hamburger icon */}
        <span className="navbar__toggle-bar" />
        <span className="navbar__toggle-bar" />
        <span className="navbar__toggle-bar" />
      </button>

      {/* Center: brand â€” stays perfectly centered */}
      <h3 className="navbar__brand" onClick={() => navigate('/dashboard')}>
        PeerFund
      </h3>

      {/* Right: actions */}
      <div className="navbar__actions">
        <div className="navbar__bell">
          <NotificationBell />
        </div>
        <button className="navbar__btn" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
