// src/features/verification/VerificationPage.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './VerificationPage.css';

const VerificationPage = () => {
  const navigate = useNavigate();
  const [status, setStatus] = useState('LOADING');
  const [notes, setNotes] = useState('');
  const [checklist, setChecklist] = useState(null);
  const [error, setError] = useState('');
  const [uploading, setUploading] = useState(false);

  const token = localStorage.getItem('token');

  const fetchStatus = async () => {
    if (!token) {
      setError('Not logged in');
      setStatus('ERROR');
      return;
    }

    try {
      setError('');
      const res = await fetch('/api/verification/status', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || 'Failed to load verification status');
      }

      const data = await res.json();
      setChecklist(data);
      setStatus(data.status || 'PENDING');
      setNotes(data.notes || '');
    } catch (err) {
      console.error('VerificationPage fetchStatus error:', err);
      setError(err.message || 'Failed to load verification status');
      setStatus('ERROR');
    }
  };

  useEffect(() => {
    fetchStatus();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    if (status === 'APPROVED') {
      // If already verified, go to dashboard
      navigate('/dashboard', { replace: true });
    }
  }, [status, navigate]);

  const uploadOne = async (fieldName, endpoint) => {
    if (!token) return;
    const fileInput = document.getElementById(fieldName);
    if (!fileInput || !fileInput.files || !fileInput.files[0]) {
      alert('Please choose a file first.');
      return;
    }

    const file = fileInput.files[0];
    const formData = new FormData();
    formData.append('file', file);

    try {
      setUploading(true);
      setError('');

      const res = await fetch(endpoint, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const text = await res.text();
      if (!res.ok) {
        throw new Error(text || 'Upload failed');
      }

      let data = {};
      try {
        data = JSON.parse(text);
      } catch {
        // ignore JSON parse error if backend returns plain text
      }

      if (data.checklist) {
        setChecklist(data.checklist);
        setStatus(data.checklist.status || status);
      } else {
        // refresh status from backend
        await fetchStatus();
      }

      alert('Upload successful.');
    } catch (err) {
      console.error('Upload error:', err);
      setError(err.message || 'Upload failed');
    } finally {
      setUploading(false);
    }
  };

  const renderStatusMessage = () => {
    if (status === 'LOADING') return 'Checking your verification status…';
    if (status === 'PENDING') {
      return 'Your verification is pending. Please make sure you have uploaded all required photos.';
    }
    if (status === 'REJECTED') {
      return (
        <>
          Your verification was rejected. Please re-upload clear, readable photos of your ID
          and selfie.
          {notes ? <div style={{ marginTop: 8 }}>Admin note: {notes}</div> : null}
        </>
      );
    }
    if (status === 'APPROVED') {
      return 'You are verified! Redirecting to dashboard…';
    }
    return 'Unknown status.';
  };

  const allUploaded =
    checklist?.hasIdFront && checklist?.hasIdBack && checklist?.hasSelfie;

  const handleSubmitForReview = async () => {
    // In your backend, uploading already sets status to PENDING,
    // so this is mainly a "refresh + confirmation" UX button.
    await fetchStatus();
    alert(
      'Your photos are saved. An admin will review your account shortly. You will get full access once you are approved.'
    );
  };

  return (
    <div className="verify-shell">
      <div className="verify-card">
        {/* Top bar with Back button */}
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            marginBottom: 12,
          }}
        >
          <button
            type="button"
            onClick={() => navigate('/dashboard')}
            style={{
              border: 'none',
              background: 'transparent',
              cursor: 'pointer',
              padding: 0,
              color: '#0f172a',
              display: 'inline-flex',
              alignItems: 'center',
              gap: 4,
            }}
          >
            <span style={{ fontSize: 18 }}>←</span>
            <span>Back</span>
          </button>
          <span style={{ fontSize: 12, color: '#64748b' }}>
            Step 1 of 1 – Verify your identity
          </span>
        </div>

        <h1>Verify your identity</h1>
        <p className="verify-status">{renderStatusMessage()}</p>

        {error && <div className="verify-error">{error}</div>}

        <div className="verify-grid">
          {/* ID Front */}
          <div className="verify-field">
            <h3>Front of ID</h3>
            <p>Upload a clear photo of the front of your government-issued ID.</p>
            <input type="file" id="idFront" accept="image/*" />
            <button
              type="button"
              onClick={() =>
                uploadOne('idFront', '/api/verification/id/front')
              }
              disabled={uploading}
            >
              Upload front of ID
            </button>
            {checklist?.hasIdFront && (
              <div className="verify-ok">✓ Front of ID on file</div>
            )}
          </div>

          {/* ID Back */}
          <div className="verify-field">
            <h3>Back of ID</h3>
            <p>Upload a clear photo of the back of your ID.</p>
            <input type="file" id="idBack" accept="image/*" />
            <button
              type="button"
              onClick={() =>
                uploadOne('idBack', '/api/verification/id/back')
              }
              disabled={uploading}
            >
              Upload back of ID
            </button>
            {checklist?.hasIdBack && (
              <div className="verify-ok">✓ Back of ID on file</div>
            )}
          </div>

          {/* Selfie */}
          <div className="verify-field">
            <h3>Selfie</h3>
            <p>Upload a selfie of your face. Make sure your face is clearly visible.</p>
            <input type="file" id="selfie" accept="image/*" />
            <button
              type="button"
              onClick={() =>
                uploadOne('selfie', '/api/verification/selfie')
              }
              disabled={uploading}
            >
              Upload selfie
            </button>
            {checklist?.hasSelfie && (
              <div className="verify-ok">✓ Selfie on file</div>
            )}
          </div>
        </div>

        {/* Big "Submit" button at the bottom */}
        <div style={{ marginTop: 24 }}>
          <button
            type="button"
            onClick={handleSubmitForReview}
            disabled={!allUploaded || uploading}
            style={{
              padding: '8px 16px',
              borderRadius: 999,
              border: 'none',
              cursor: allUploaded && !uploading ? 'pointer' : 'not-allowed',
              background: allUploaded && !uploading ? '#0f172a' : '#94a3b8',
              color: '#f9fafb',
              fontWeight: 500,
            }}
          >
            {allUploaded
              ? 'Submit for review'
              : 'Upload all three photos to submit'}
          </button>
        </div>

        <p className="verify-footnote">
          Once all three photos are uploaded and submitted, an admin will review
          your account. You’ll get full access to PeerFund after approval.
        </p>
      </div>
    </div>
  );
};

export default VerificationPage;
