// src/features/wallet/DepositModal.jsx
import React, { useMemo, useState } from 'react';
import { createDepositIntent, devConfirmDeposit } from './walletApi';

// Stripe (only used when backend returns clientSecret)
import { loadStripe } from '@stripe/stripe-js';
import { Elements, PaymentElement, useStripe, useElements } from '@stripe/react-stripe-js';

// IMPORTANT: add your publishable key in .env (Vite) or .env(.local)
// VITE_STRIPE_PUBLISHABLE_KEY=pk_test_123...
const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLISHABLE_KEY || '');

export default function DepositModal({ onClose, onSuccess }) {
  const [amount, setAmount] = useState('25.00');
  const [error, setError] = useState('');
  const [busy, setBusy] = useState(false);

  // Step state
  const [clientSecret, setClientSecret] = useState(null);
  const [ledgerId, setLedgerId] = useState(null);

  const hasStripe = Boolean(clientSecret);

  const onContinue = async () => {
    setError('');
    // Parse dollars from "$ 25.00"
    const num = Number(String(amount).replace(/[$,\s]/g, ''));
    if (!Number.isFinite(num) || num <= 0) {
      setError('Please enter a valid amount.');
      return;
    }

    setBusy(true);
    try {
      const resp = await createDepositIntent(num);
      if (resp?.clientSecret) {
        // Go to Stripe step
        setClientSecret(resp.clientSecret);
        setLedgerId(resp.ledgerId || null);
      } else if (resp?.simulated && resp?.ledgerId) {
        // No Stripe configured: immediately confirm in dev
        const ok = await devConfirmDeposit(resp.ledgerId);
        onSuccess?.(ok);
        onClose?.();
      } else {
        setError('Unexpected response from server.');
      }
    } catch (e) {
      setError(e.message || 'Could not create deposit.');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="modal">
      <div className="modal-header">
        <h3>Deposit to PeerFund Balance</h3>
        <button className="icon-btn" onClick={onClose} aria-label="Close">×</button>
      </div>

      {!hasStripe ? (
        <>
          {error && <div className="form-error">{error}</div>}
          <label className="label">Amount</label>
          <div className="input-prefix">
            <span>$</span>
            <input
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              inputMode="decimal"
              placeholder="25.00"
            />
          </div>

          <div className="modal-actions">
            <button type="button" onClick={onClose} disabled={busy}>Cancel</button>
            <button
              type="button"
              className="btn btn--primary"
              onClick={onContinue}
              disabled={busy}
            >
              {busy ? 'Processing...' : 'Continue'}
            </button>
          </div>
        </>
      ) : (
        // Stripe step
        <StripeStep
          clientSecret={clientSecret}
          onClose={onClose}
          onSuccess={onSuccess}
        />
      )}
    </div>
  );
}

/** Renders the Payment Element + confirms the payment (no redirects for cards). */
function StripeStep({ clientSecret, onClose, onSuccess }) {
  const options = useMemo(() => ({ clientSecret, appearance: { theme: 'stripe' } }), [clientSecret]);

  return (
    <Elements stripe={stripePromise} options={options}>
      <PaymentContent onClose={onClose} onSuccess={onSuccess} />
    </Elements>
  );
}

function PaymentContent({ onClose, onSuccess }) {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState('');
  const [busy, setBusy] = useState(false);

  const confirm = async () => {
    setError('');
    if (!stripe || !elements) return;

    setBusy(true);
    try {
      const { error: payErr } = await stripe.confirmPayment({
        elements,
        // For card payments we can avoid a full-page redirect:
        redirect: 'if_required',
        // (Optional) If your flow needs a return_url for certain PMs:
        // confirmParams: { return_url: window.location.origin + '/deposit/complete' },
      });

      if (payErr) {
        setError(payErr.message || 'Payment failed');
      } else {
        onSuccess?.(true); // let parent refresh wallet
        onClose?.();
      }
    } catch (e) {
      setError(e.message || 'Payment failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <>
      <div style={{ marginBottom: 12 }}>
        <PaymentElement />
      </div>
      {error && <div className="form-error">{error}</div>}
      <div className="modal-actions">
        <button type="button" onClick={onClose} disabled={busy}>Cancel</button>
        <button
          type="button"
          className="btn btn--primary"
          onClick={confirm}
          disabled={busy || !stripe || !elements}
        >
          {busy ? 'Processing…' : 'Pay'}
        </button>
      </div>
    </>
  );
}
