// src/features/wallet/WalletPanel.jsx
import React, { useState } from 'react';
import { fetchWallet } from './walletApi';

export default function WalletPanel({ onClose, onBalanceUpdated }) {
  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);

  const token = localStorage.getItem('token')?.replace(/^"|"$/g, '') || '';

  const deposit = async () => {
    try {
      setBusy(true);
      const n = Number(String(amount).replace(/[^0-9.]/g, ''));
      if (!Number.isFinite(n) || n <= 0) {
        alert('Enter a valid amount');
        return;
      }

      const r = await fetch('/api/wallet/deposit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ amount: n.toFixed(2) }),
      });

      const text = await r.text();
      let data = {};
      try { data = text ? JSON.parse(text) : {}; } catch {}

      if (!r.ok) {
        alert(data?.error || text || 'Deposit failed');
        return;
      }

      const w = await fetchWallet();
      onBalanceUpdated?.(w.availableCents);
      onClose?.();
    } catch (e) {
      console.error(e);
      alert(e.message || 'Deposit failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="wallet-panel">
      <h3>Add funds</h3>
      <div className="row">
        <label>Amount</label>
        <div className="input-wrap">
          <span>$</span>
          <input
            value={amount}
            onChange={e => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
          />
        </div>
      </div>
      <div className="row right">
        <button className="btn" onClick={onClose}>Cancel</button>
        <button className="btn primary" onClick={deposit} disabled={busy}>
          {busy ? 'Processing…' : 'Continue'}
        </button>
      </div>
    </div>
  );
}
