// src/features/wallet/WalletBadge.jsx
import React, { useEffect, useState } from 'react';
import { fetchWallet } from './walletApi';
import DepositModal from './DepositModal';
import './wallet.css';

export default function WalletBadge() {
  const [wallet, setWallet] = useState({ available: 0, pending: 0 });
  const [show, setShow] = useState(false);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    try {
      setLoading(true);
      const w = await fetchWallet();
      setWallet(w);
    } catch (e) {
      // silent fail if unauthenticated
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  const fmt = (n) => `$${Number(n || 0).toFixed(2)}`;

  return (
    <>
      <button className="pfb-badge" onClick={() => setShow(true)} title="PeerFund Balance">
        <span className="pfb-chip">Balance</span>
        <span className="pfb-amount">{loading ? '—' : fmt(wallet.available)}</span>
      </button>

      {show && (
        <DepositModal
          onClose={() => setShow(false)}
          onDeposited={load}
        />
      )}
    </>
  );
}
