// src/components/UserProfileModal.jsx
import React, { useEffect, useState } from 'react';
import './UserProfileModal.css';
// ðŸ‘‡ add this import (path is from /components to /features)
import DirectRequestForm from '../features/direct/DirectRequestForm';

function fmtMoney(n) {
  const v = Number(n || 0);
  return isFinite(v) ? `$${v.toFixed(2)}` : 'â€”';
}

// helper: decode JWT to get my user id
function getCurrentUserId() {
  const t = localStorage.getItem('token');
  if (!t) return null;
  try {
    const payload = JSON.parse(atob(t.split('.')[1] || ''));
    return payload?.userId ?? null;
  } catch {
    return null;
  }
}

export default function UserProfileModal({ userId, onClose }) {
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState('');
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    if (!userId) return;
    let alive = true;

    (async () => {
      try {
        setLoading(true);
        setErr('');
        const token = localStorage.getItem('token') || '';
        const res = await fetch(`/api/users/${userId}/profile`, {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });
        if (!res.ok) throw new Error(await res.text());
        const data = await res.json();
        if (alive) setProfile(data);
      } catch (e) {
        if (alive) setErr('Failed to load profile.');
        console.error(e);
      } finally {
        if (alive) setLoading(false);
      }
    })();

    return () => { alive = false; };
  }, [userId]);

  if (!userId) return null;

  const me = getCurrentUserId();
  const isMyself = me && profile?.id && String(me) === String(profile.id);

  const initials =
    (profile?.name || '')
      .split(' ')
      .map(p => p[0])
      .join('')
      .slice(0, 2)
      .toUpperCase() || 'U';

  return (
    <div className="pfm-backdrop" onClick={onClose}>
      <div
        className="pfm-modal pfm-animate-in"
        onClick={(e) => e.stopPropagation()}
        role="dialog"
        aria-modal="true"
      >
        <button className="pfm-close" onClick={onClose} aria-label="Close">âœ•</button>

        {loading ? (
          <div className="pfm-body pfm-center">
            <div className="pfm-spinner" />
          </div>
        ) : err ? (
          <div className="pfm-body pfm-error">{err}</div>
        ) : (
          <div className="pfm-body">
            <header className="pfm-header">
              <div className="pfm-avatar">{initials}</div>
              <div className="pfm-header-meta">
                <div className="pfm-name">
                  {profile?.name || 'Unknown'}
                  {profile?.isSuperUser && <span className="pfm-badge">SuperUser</span>}
                </div>
                <div className="pfm-sub">
                  {profile?.location ? <span>{profile.location} â€¢ </span> : null}
                  Member since:{' '}
                  {profile?.signupDate ? new Date(profile.signupDate).toLocaleDateString() : 'â€”'}
                </div>
                {profile?.lastActiveAt && (
                  <div className="pfm-last-active">
                    Last active: {new Date(profile.lastActiveAt).toLocaleString()}
                  </div>
                )}
              </div>
            </header>

            {profile?.summary && (
              <>
                <div className="pfm-section-title">About</div>
                <p className="pfm-summary">{profile.summary}</p>
              </>
            )}

            <div className="pfm-section-title">Stats</div>
            <div className="pfm-stats">
              <div className="pfm-stat">
                <div className="pfm-stat-label">Loans Given</div>
                <div className="pfm-stat-value">
                  {profile?.stats?.loansGivenCount ?? 0}
                </div>
                <div className="pfm-stat-sub">
                  {fmtMoney(profile?.stats?.totalLent)}
                </div>
              </div>

              <div className="pfm-stat">
                <div className="pfm-stat-label">Loans Received</div>
                <div className="pfm-stat-value">
                  {profile?.stats?.loansReceivedCount ?? 0}
                </div>
                <div className="pfm-stat-sub">
                  {fmtMoney(profile?.stats?.totalBorrowed)}
                </div>
              </div>

              <div className="pfm-stat">
                <div className="pfm-stat-label">Open Requests</div>
                <div className="pfm-stat-value">
                  {profile?.stats?.openRequestsCount ?? 0}
                </div>
                <div className="pfm-stat-sub">currently open</div>
              </div>
            </div>

            {/* --- Direct loan request section (hidden on your own profile) --- */}
            {!isMyself && (
              <>
                <div className="pfm-section-title" style={{ marginTop: 16 }}>
                  Request a Loan from {profile?.name?.split(' ')[0] || 'this user'}
                </div>
                <div className="pfm-request-wrap">
                  {/* 
                    Optional: pass pre-fill terms if you expose them from backend:
                    initialRate={profile?.lenderTerms?.rate}
                    minAmount={profile?.lenderTerms?.minAmount}
                    maxAmount={profile?.lenderTerms?.maxAmount}
                  */}
                  <DirectRequestForm lenderId={profile.id} />
                </div>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
