// src/features/money/MoneySummary.jsx
import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './MoneySummary.css';
import UserProfileModal from '../../components/UserProfileModal';
import LoanThreadModal from '../loans/LoanThreadModal';

// helper: decode JWT for current user id
function getCurrentUserId() {
  const t = localStorage.getItem('token');
  if (!t) return null;
  try {
    const payload = JSON.parse(atob(t.split('.')[1] || ''));
    return payload?.userId ?? null;
  } catch {
    return null;
  }
}

const fmtMoney = (n) =>
  typeof n === 'number' && !Number.isNaN(n) ? `$${n.toFixed(2)}` : 'â€”';

// One-line row layout used across all sections
const ROW_GRID = {
  display: 'grid',
  gridTemplateColumns: 'minmax(160px,1fr) 140px 180px auto auto',
  alignItems: 'center',
  gap: 16,
};

/* ----------------------- authFetch: token + cookies ----------------------- */
async function authFetch(url, options = {}) {
  const token = localStorage.getItem('token');
  const headers = {
    ...(options.headers || {}),
    ...(token ? { Authorization: `Bearer ${token}` } : {}),
  };
  const res = await fetch(url, {
    ...options,
    headers,
    credentials: 'include', // send cookies if server uses them
  });
  if (res.status === 401) {
    // Bounce to login on unauthorized
    try {
      console.warn('401 on', url, await res.text());
    } catch {}
    window.location.assign('/login');
    throw new Error('Unauthorized');
  }
  return res;
}

/* ---------- Child component for a single open request row (with negotiation) ---------- */
function OpenRequestRow({
  req,
  isExpanded,
  onToggle,
  actionLoading,
  submitCounter,
  acceptRequest,
  declineRequest,
}) {
  const [proposal, setProposal] = useState({
    amount: req.amount || 0,
    months: req.duration || 1,
    interestRate: req.interestRate || 0,
  });

  // Lender is usually the one who can "Accept" a direct request
  const canAccept =
    req.type === 'direct' ? req.role === 'Lending' : true; // keep market permissive unless your API restricts it

  return (
    <div className="ms-card" key={req.id}>
      {/* ONE-LINE TOP ROW */}
      <div className="ms-card-row" style={ROW_GRID}>
        <div>
          <div className="ms-label">Counterparty</div>
          <div className="ms-value-strong">
            {req.counterpartyName || 'Unknown'}
            <span style={{ marginLeft: 8, color: '#64748b', fontWeight: 600 }}>
              â€¢ {req.role}
            </span>
          </div>
        </div>

        <div>
          <div className="ms-label">Amount</div>
          <div className="ms-value-strong">{fmtMoney(req.amount)}</div>
        </div>

        <div>
          <div className="ms-label">Created</div>
          <div className="ms-value-strong">
            {req.createdAt ? new Date(req.createdAt).toLocaleDateString() : 'â€”'}
          </div>
        </div>

        <div style={{ justifySelf: 'end' }}>
          <span className="ms-badge ms-badge--warn">Open</span>
        </div>

        <div style={{ justifySelf: 'end' }}>
          <button className="action-btn" onClick={onToggle} title="View request details">
            {isExpanded ? 'ðŸ”½ Hide' : 'ðŸ“„ View'}
          </button>
        </div>
      </div>

      {/* DETAILS + NEGOTIATE BAR */}
      {isExpanded && (
        <div className="ms-details">
          <p className="mt-6">
            <strong>Your role:</strong> {req.role}
          </p>
          <p className="mt-6">
            <strong>Status:</strong> {req.status ?? 'OPEN'}
          </p>
          <p className="mt-6">
            <strong>Interest Rate:</strong>{' '}
            {Number.isFinite(req.interestRate) ? `${req.interestRate}%` : 'â€”'}
          </p>
          <p className="mt-6">
            <strong>Duration:</strong>{' '}
            {Number.isFinite(req.duration) ? `${req.duration} months` : 'â€”'}
          </p>
          {req.purpose ? (
            <p className="mt-6">
              <strong>Reason:</strong> {req.purpose}
            </p>
          ) : null}
          {req.lenderName ? (
            <p className="mt-6">
              <strong>Requested Lender:</strong> {req.lenderName}
            </p>
          ) : null}

          {/* Negotiate / Accept controls */}
          <div className="mt-8" style={{ display: 'flex', flexWrap: 'wrap', gap: 10 }}>
            <div>
              <label className="ms-label">Amount</label>
              <input
                type="number"
                min="1"
                className="auth-input"
                value={proposal.amount}
                onChange={(e) =>
                  setProposal((p) => ({ ...p, amount: Number(e.target.value) }))
                }
                style={{ width: 140 }}
              />
            </div>
            <div>
              <label className="ms-label">APR %</label>
              <input
                type="number"
                min="0"
                className="auth-input"
                value={proposal.interestRate}
                onChange={(e) =>
                  setProposal((p) => ({ ...p, interestRate: Number(e.target.value) }))
                }
                style={{ width: 120 }}
              />
            </div>
            <div>
              <label className="ms-label">Months</label>
              <input
                type="number"
                min="1"
                className="auth-input"
                value={proposal.months}
                onChange={(e) =>
                  setProposal((p) => ({ ...p, months: Number(e.target.value) }))
                }
                style={{ width: 120 }}
              />
            </div>

            <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
              <button
                className="action-btn"
                onClick={() =>
                  submitCounter(
                    req,
                    req.type === 'direct'
                      ? {
                          amount: proposal.amount,
                          apr: proposal.interestRate,
                          months: proposal.months,
                        }
                      : {
                          amount: proposal.amount,
                          interestRate: proposal.interestRate,
                          months: proposal.months,
                        }
                  )
                }
                disabled={!!actionLoading[req.id]}
              >
                {actionLoading[req.id] ? 'Savingâ€¦' : 'Make Counter-Offer'}
              </button>

              <button
                className="action-btn primary"
                onClick={() => canAccept && acceptRequest(req)}
                disabled={!!actionLoading[req.id] || !canAccept}
                title={canAccept ? 'Accept this request' : 'Only the lender can accept'}
              >
                {actionLoading[req.id] ? 'Workingâ€¦' : 'Accept'}
              </button>

              <button
                className="action-btn"
                onClick={() => declineRequest(req)}
                disabled={!!actionLoading[req.id]}
              >
                {actionLoading[req.id] ? 'Workingâ€¦' : 'Decline'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

/* -------------------------------- Parent -------------------------------- */

const MoneySummary = () => {
  const [summary, setSummary] = useState({
    loansGiven: [],
    loansReceived: [],
    myOpenRequests: [],
  });
  const [loading, setLoading] = useState(true);

  const [expandedLoans, setExpandedLoans] = useState({});
  const [openSec, setOpenSec] = useState({ open: true, given: true, received: true });

  const [payLoading, setPayLoading] = useState({});
  const [actionLoading, setActionLoading] = useState({});
  const [profileUserId, setProfileUserId] = useState(null);
  const [threadLoanId, setThreadLoanId] = useState(null);

  const navigate = useNavigate(); // kept if you need to navigate elsewhere later

  /* ------------------ Fetch helpers (market + direct) ------------------ */

  const fetchMarketOpenRequests = async () => {
  const me = getCurrentUserId();
  if (!me) return [];

  // Prefer a consolidated endpoint if your backend has one
  try {
    const r = await authFetch('/api/loan-requests/open/mine');
    if (r.ok) {
      const items = await r.json();
      const list = Array.isArray(items) ? items : items?.items || [];
      return list.map((req) => {
        const borrowerId = req.borrowerId || req?.borrower?.id || me;
        const borrowerName = req?.borrower?.name || (borrowerId === me ? 'You' : 'Borrower');
        const lenderName = req?.lender?.name || req?.acceptedOffer?.lender?.name || '';
        return {
          type: 'market',
          id: req.id,
          amount: Number(req.amount),
          duration: Number(req.duration ?? req.months),
          interestRate: Number(req.interestRate ?? req.apr),
          purpose: req.purpose || '',
          createdAt: req.createdAt || null,
          borrowerName,
          borrowerId,
          lenderName,
          counterpartyName: lenderName || 'â€”',
          role: borrowerId === me ? 'Borrowing' : 'Lending', // usually Borrowing in market
          status: req.status || 'OPEN',
        };
      });
    }
  } catch {}

  // Fallback to your prior calls (borrower-only)
  try {
    const r = await authFetch('/api/loan-requests/mine?status=OPEN');
    if (r.ok) {
      const list = await r.json();
      const items = Array.isArray(list) ? list : Array.isArray(list?.items) ? list.items : [];
      return items
        .filter((it) => (it?.borrowerId || it?.borrower?.id) === me)
        .map((req) => ({
          type: 'market',
          id: req.id,
          amount: Number(req.amount),
          duration: Number(req.duration ?? req.months),
          interestRate: Number(req.interestRate ?? req.apr),
          purpose: req.purpose || '',
          createdAt: req.createdAt || null,
          borrowerName: req?.borrower?.name || 'You',
          borrowerId: req?.borrower?.id || req.borrowerId || me,
          lenderName: req?.lender?.name || '',
          counterpartyName: req?.lender?.name || 'â€”',
          role: 'Borrowing',
          status: req.status || 'OPEN',
        }));
    }
  } catch {}

  // Fallback 2 (older open loans list)
  try {
    const resOpen = await authFetch('/api/loans/open');
    if (resOpen.ok) {
      const openListRaw = await resOpen.json();
      const openList = Array.isArray(openListRaw)
        ? openListRaw
        : Array.isArray(openListRaw?.items)
        ? openListRaw.items
        : [];
      const mine = openList.filter((r) => {
        const borrowerId = r?.borrower?.id || r?.borrowerId;
        return borrowerId && borrowerId === me;
      });
      return mine.map((r) => ({
        type: 'market',
        id: r.id,
        amount: Number(r.amount),
        duration: Number(r.duration ?? r.months),
        interestRate: Number(r.interestRate ?? r.apr),
        purpose: r.purpose || '',
        createdAt: r.createdAt || null,
        borrowerName: r?.borrower?.name || 'You',
        borrowerId: r?.borrower?.id || r?.borrowerId || me,
        lenderName: r?.lender?.name || '',
        counterpartyName: r?.lender?.name || 'â€”',
        role: 'Borrowing',
        status: r.status || 'OPEN',
      }));
    }
  } catch {}

  return [];
}

  const fetchDirectOpenRequests = async () => {
  const me = getCurrentUserId();
  if (!me) return [];

  try {
    // Use the consolidated view we built on the backend
    const r = await authFetch('/api/direct-requests/open/mine');
    if (!r.ok) return [];

    const rows = await r.json();
    // rows already include borrower/lender sub-objects
    return (Array.isArray(rows) ? rows : []).map((d) => {
      const borrowerId = d.borrowerId || d?.borrower?.id;
      const lenderId = d.lenderId || d?.lender?.id;
      const iAmBorrower = String(borrowerId) === String(me);
      const role = iAmBorrower ? 'Borrowing' : 'Lending';
      const counterpartyName = iAmBorrower
        ? d?.lender?.name || 'Lender'
        : d?.borrower?.name || 'Borrower';

      return {
        type: 'direct',
        id: d.id,
        amount: Number(d.amount),
        duration: Number(d.months ?? d.duration),
        interestRate: Number(d.apr ?? d.interestRate),
        createdAt: d.createdAt || null,
        borrowerId,
        borrowerName: d?.borrower?.name || (iAmBorrower ? 'You' : ''),
        lenderId,
        lenderName: d?.lender?.name || (!iAmBorrower ? 'You' : ''),
        counterpartyName,
        role,                        // <-- Borrowing | Lending
        status: d.status || 'PENDING',
      };
    });
  } catch {
    return [];
  }
};


  const fetchSummary = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return;
    setLoading(true);
    try {
      const res = await authFetch('/api/users/my-money-summary');
      let loansGiven = [];
      let loansReceived = [];
      if (res.ok) {
        const data = await res.json();
        loansGiven = Array.isArray(data.loansGiven)
          ? data.loansGiven
          : Array.isArray(data.loansAsLender)
          ? data.loansAsLender
          : [];
        loansReceived = Array.isArray(data.loansReceived)
          ? data.loansReceived
          : Array.isArray(data.loansAsBorrower)
          ? data.loansAsBorrower
          : [];
      }

      const [marketOpen, directOpen] = await Promise.all([
        fetchMarketOpenRequests(),
        fetchDirectOpenRequests(),
      ]);
      const myOpenRequests = [...marketOpen, ...directOpen];

      setSummary({ loansGiven, loansReceived, myOpenRequests });
    } catch (err) {
      console.error('Error fetching money summary:', err);
      setSummary({ loansGiven: [], loansReceived: [], myOpenRequests: [] });
    } finally {
      setLoading(false);
    }
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    fetchSummary();
  }, [fetchSummary]);

  const loanKey = (loan, idx) => loan?.id || loan?._id || String(idx);

  const toggleLoanDetails = (id) => {
    setExpandedLoans((prev) => ({
      ...(Object.keys(prev).reduce((acc, k) => ({ ...acc, [k]: false }), {})),
      [id]: !prev[id],
    }));
  };

  const payNext = async (loan) => {
    const key = loanKey(loan);
    const amount = loan?.installmentAmount ?? 0;
    if (!window.confirm(`Pay the next installment (${fmtMoney(amount)})?`)) return;
    try {
      setPayLoading((m) => ({ ...m, [key]: true }));
      const res = await authFetch(`/api/loans/${loan.id || loan._id}/pay-next`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || 'Payment failed');
      const paid = typeof data.amount === 'number' ? data.amount : amount;
      alert(`Payment successful! Amount: ${fmtMoney(paid)}`);
      await fetchSummary();
    } catch (e) {
      alert(e.message);
    } finally {
      setPayLoading((m) => ({ ...m, [key]: false }));
    }
  };

  /* -------------------- Action endpoints + helpers -------------------- */

  const actionPaths = {
    market: {
      accept: (id) => [
        `/api/loan-requests/${id}/accept`,
        `/api/loan-requests/${id}/approve`,
        `/api/loan-requests/${id}/respond?action=ACCEPT`,
      ],
      decline: (id) => [
        `/api/loan-requests/${id}/decline`,
        `/api/loan-requests/${id}/reject`,
        `/api/loan-requests/${id}/respond?action=DECLINE`,
      ],
      counter: (id) => [
        `/api/loan-requests/${id}/counter-offer`,
        `/api/loan-requests/${id}/counter`,
        `/api/loan-requests/${id}/respond?action=COUNTER`,
      ],
    },
    direct: {
      accept: (id) => [
        `/api/direct-requests/${id}/accept`,
        `/api/direct-requests/${id}/approve`,
        `/api/direct-requests/${id}/respond?action=ACCEPT`,
      ],
      decline: (id) => [
        `/api/direct-requests/${id}/decline`,
        `/api/direct-requests/${id}/reject`,
        `/api/direct-requests/${id}/respond?action=DECLINE`,
      ],
      counter: (id) => [
        `/api/direct-requests/${id}/counter-offer`,
        `/api/direct-requests/${id}/counter`,
        `/api/direct-requests/${id}/respond?action=COUNTER`,
      ],
    },
  };

  async function tryVerbs(url, body) {
    const optionsBase = {
      headers: { 'Content-Type': 'application/json' },
      body: body ? JSON.stringify(body) : undefined,
    };
    const verbs = ['POST', 'PUT', 'PATCH'];
    let lastErr = null;
    for (const method of verbs) {
      try {
        const r = await authFetch(url, { method, ...optionsBase });
        if (r.ok) return r;
        const txt = await r.text().catch(() => '');
        lastErr = new Error(`${r.status} ${r.statusText}${txt ? ` â€¢ ${txt}` : ''}`);
      } catch (e) {
        lastErr = e;
      }
    }
    throw lastErr || new Error('All methods failed');
  }

  const callAction = async (type, id, which, body) => {
    setActionLoading((m) => ({ ...m, [id]: true }));
    try {
      const urls = actionPaths[type]?.[which]?.(id) || [];
      if (!urls.length) throw new Error(`Unknown action: ${type}.${which}`);

      const supersetBody = body
        ? {
            ...body,
            amount: body.amount ?? body.value ?? undefined,
            months: body.months ?? body.duration ?? undefined,
            duration: body.duration ?? body.months ?? undefined,
            interestRate: body.interestRate ?? body.apr ?? undefined,
            apr: body.apr ?? body.interestRate ?? undefined,
          }
        : undefined;

      let ok = false;
      let lastError = null;

      for (const url of urls) {
        try {
          await tryVerbs(url, supersetBody);
          ok = true;
          break;
        } catch (e) {
          lastError = e;
        }
      }

      if (!ok) throw lastError || new Error('No endpoint accepted this action');
      await fetchSummary();
    } catch (e) {
      console.error(e);
      alert(`Action failed:\n${e.message || e}`);
    } finally {
      setActionLoading((m) => ({ ...m, [id]: false }));
    }
  };

  const acceptRequest = (req) => callAction(req.type, req.id, 'accept');
  const declineRequest = (req) => callAction(req.type, req.id, 'decline');
  const submitCounter = (req, proposal) => callAction(req.type, req.id, 'counter', proposal);

  /* ------------------------------- Renderers ------------------------------- */

  const renderLoanCard = (loan, isLender, idx) => {
    const key = loanKey(loan, idx);
    const isExpanded = !!expandedLoans[key];
    const otherParty = isLender ? loan.borrowerName : loan.lenderName;

    const canPay =
      !isLender &&
      typeof loan.installmentAmount === 'number' &&
      loan.installmentAmount > 0 &&
      !!loan.nextDueDate;

    const badgeClass =
      (loan.status || '').toUpperCase() === 'FUNDED'
        ? 'ms-badge ms-badge--ok'
        : 'ms-badge ms-badge--warn';

    const fundedDate =
      loan.fundedDate ? new Date(loan.fundedDate).toLocaleDateString() : 'â€”';

    return (
      <div className="ms-card" key={key}>
        <div className="ms-card-row" style={ROW_GRID}>
          <div>
            <div className="ms-label">{isLender ? 'Borrower' : 'Lender'}</div>
            <div className="ms-value-strong">{otherParty || 'Unknown'}</div>
          </div>
          <div>
            <div className="ms-label">Amount</div>
            <div className="ms-value-strong">{fmtMoney(loan.amount)}</div>
          </div>
          <div>
            <div className="ms-label">Funded Date</div>
            <div className="ms-value-strong">{fundedDate}</div>
          </div>
          <div style={{ justifySelf: 'end' }}>
            <span className={badgeClass}>{loan.status || 'â€”'}</span>
          </div>
          <div style={{ justifySelf: 'end', display: 'flex', gap: 8 }}>
            <button
              onClick={() => setThreadLoanId(loan.id || loan._id)}
              className="action-btn"
              title="Open conversation"
            >
              ðŸ’¬ Messages
            </button>
            <button onClick={() => toggleLoanDetails(key)} className="action-btn" title="View contract">
              {isExpanded ? 'ðŸ”½ Hide' : 'ðŸ“„ View'}
            </button>
          </div>
        </div>

        {isExpanded && (
          <div className="ms-details">
            {loan.status && (
              <p className="mt-6">
                <strong>Status:</strong> {loan.status}
              </p>
            )}
            <p className="mt-6">
              <strong>Interest Rate:</strong>{' '}
              {typeof loan.interestRate === 'number' ? `${loan.interestRate}%` : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Duration:</strong>{' '}
              {typeof loan.duration === 'number' ? `${loan.duration} months` : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Next Due Date:</strong>{' '}
              {loan.nextDueDate ? new Date(loan.nextDueDate).toLocaleDateString() : 'â€”'}
            </p>
            <p className="mt-6">
              <strong>Installment (this period):</strong> {fmtMoney(loan.installmentAmount)}
            </p>

            {isLender && typeof loan.amountCollected === 'number' && (
              <p className="mt-6">
                <strong>Amount collected on loan:</strong> {fmtMoney(loan.amountCollected)}
              </p>
            )}

            {!isLender && canPay && (
              <div className="mt-6">
                <button
                  className="action-btn primary"
                  onClick={() => payNext(loan)}
                  disabled={!!payLoading[key]}
                  title="Pay next installment"
                >
                  {payLoading[key] ? 'Processingâ€¦' : 'ðŸ’³ Make Payment'}
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  /* ------------------------------- Render ------------------------------- */

  if (loading) return <p className="ms-loading ms-container">Loading money summary...</p>;

  const given = Array.isArray(summary.loansGiven) ? summary.loansGiven : [];
  const received = Array.isArray(summary.loansReceived) ? summary.loansReceived : [];
  const myOpen = Array.isArray(summary.myOpenRequests) ? summary.myOpenRequests : [];

  return (
    <div className="ms-container">
      <h2 className="ms-heading">ðŸ’µ My Money Summary</h2>

      {/* Section: Open Requests */}
      <div className="ms-section">
        <button
          className="ms-section-head"
          onClick={() => setOpenSec((s) => ({ ...s, open: !s.open }))}
          aria-expanded={openSec.open}
        >
          <div className="ms-subheading">My Open Loan Requests</div>
          <div className="ms-caret">{openSec.open ? 'â–¾' : 'â–¸'}</div>
        </button>
        {openSec.open && (
          <div className="ms-section-body">
            {myOpen.length === 0 ? (
              <p className="ms-empty">You have no open requests.</p>
            ) : (
              myOpen.map((req) => (
                <OpenRequestRow
                  key={req.id}
                  req={req}
                  isExpanded={!!expandedLoans[req.id]}
                  onToggle={() => toggleLoanDetails(req.id)}
                  actionLoading={actionLoading}
                  submitCounter={submitCounter}
                  acceptRequest={acceptRequest}
                  declineRequest={declineRequest}
                />
              ))
            )}
          </div>
        )}
      </div>

      {/* Section: Loans Given */}
      <div className="ms-section">
        <button
          className="ms-section-head"
          onClick={() => setOpenSec((s) => ({ ...s, given: !s.given }))}
          aria-expanded={openSec.given}
        >
          <div className="ms-subheading">Loans Given (I'm the Lender)</div>
          <div className="ms-caret">{openSec.given ? 'â–¾' : 'â–¸'}</div>
        </button>
        {openSec.given && (
          <div className="ms-section-body">
            {given.length === 0 ? (
              <p className="ms-empty">No loans given yet.</p>
            ) : (
              given.map((loan, idx) => renderLoanCard(loan, true, idx))
            )}
          </div>
        )}
      </div>

      {/* Section: Loans Received */}
      <div className="ms-section">
        <button
          className="ms-section-head"
          onClick={() => setOpenSec((s) => ({ ...s, received: !s.received }))}
          aria-expanded={openSec.received}
        >
          <div className="ms-subheading">Loans Received (I'm the Borrower)</div>
          <div className="ms-caret">{openSec.received ? 'â–¾' : 'â–¸'}</div>
        </button>
        {openSec.received && (
          <div className="ms-section-body">
            {received.length === 0 ? (
              <p className="ms-empty">No loans received yet.</p>
            ) : (
              received.map((loan, idx) => renderLoanCard(loan, false, idx))
            )}
          </div>
        )}
      </div>

      {profileUserId && (
        <UserProfileModal userId={profileUserId} onClose={() => setProfileUserId(null)} />
      )}
      {threadLoanId && (
        <LoanThreadModal loanId={threadLoanId} onClose={() => setThreadLoanId(null)} />
      )}
    </div>
  );
};

export default MoneySummary;
