import React, { useEffect, useState } from 'react';
import { fetchWallet } from './walletApi';

export default function WalletBadge({ onClick }) {
  const [cents, setCents] = useState(0);

  useEffect(() => {
    let alive = true;
    fetchWallet()
      .then(w => { if (alive) setCents(w.availableCents || 0); })
      .catch(console.warn);
    return () => { alive = false; };
  }, []);

  const dollars = (cents / 100).toLocaleString(undefined, { style: 'currency', currency: 'USD' });

  return (
    <button className="balance-pill" onClick={onClick}>
      <span className="pill-label">Balance</span>
      <span className="pill-amount">{dollars}</span>
    </button>
  );
}
