// src/features/loans/LoanDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import RepaymentForm from './RepaymentForm';
import ContractModal from '../../components/ContractModal';
import UserProfileModal from '../../components/UserProfileModal';

const LoanDetails = () => {
  const { loanId } = useParams();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [loan, setLoan] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [showRepaymentForm, setShowRepaymentForm] = useState(false);
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [showContractModal, setShowContractModal] = useState(false);

  // which offer rows are expanded
  const [expandedOffer, setExpandedOffer] = useState({});

  // Offer form: amount will be locked to loan.amount
  const [offerForm, setOfferForm] = useState({
    amount: '',
    duration: '',
    interestRate: '',
    message: '',
  });

  const token = localStorage.getItem('token');
  const userId = token ? JSON.parse(atob(token.split('.')[1])).userId : null;

  const handleOfferChange = (e) => {
    const { name, value } = e.target;
    setOfferForm((prev) => ({ ...prev, [name]: value }));
  };

  const submitOffer = async () => {
    try {
      const payload = {
        amount: Number(loan.amount), // ðŸ”’ lock to requested amount
        duration: Number(offerForm.duration),
        interestRate: Number(offerForm.interestRate),
        message: (offerForm.message || '').trim() || undefined,
      };

      // simple validation
      if (!Number.isFinite(payload.duration) || payload.duration < 1) {
        alert('Please enter a valid duration (months).');
        return;
      }
      if (!Number.isFinite(payload.interestRate) || payload.interestRate < 0) {
        alert('Please enter a valid interest rate (APR).');
        return;
      }

      const res = await fetch(`/api/loans/${loanId}/offers`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });

      if (!res.ok) throw new Error((await res.text()) || 'Failed to submit offer');
      const data = await res.json();
      alert('Offer submitted!');
      setLoan((prev) => ({
        ...prev,
        loanOffers: [data, ...(prev.loanOffers || [])],
      }));
      // keep duration/rate for convenience; clear message
      setOfferForm((f) => ({ ...f, message: '' }));
    } catch (err) {
      console.error('Error submitting offer:', err);
      alert('There was a problem submitting your offer.');
    }
  };

  const fetchLoanDetails = async () => {
    try {
      const res = await fetch(`/api/loans/${loanId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setLoan(data);
      setMessages(data.messages || []);
    } catch (err) {
      console.error('Error fetching loan details:', err);
    }
  };

  useEffect(() => {
    fetchLoanDetails();
  }, [loanId]); // eslint-friendly

  // Prefill the offer form when loan loads; amount is locked to loan.amount
  useEffect(() => {
    if (!loan) return;
    setOfferForm({
      amount: Number(loan.amount || 0),
      duration: Number(loan.duration || 1),
      interestRate: Number(loan.interestRate ?? loan.apr ?? 0),
      message: '',
    });
  }, [loan]);

  // Scroll to discussion if ?tab=discussion
  useEffect(() => {
    if (searchParams.get('tab') === 'discussion' && loan) {
      setTimeout(() => {
        const el = document.getElementById('discussion-thread');
        if (el) el.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }, 0);
    }
  }, [searchParams, loan]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;
    try {
      const res = await fetch(`/api/loans/${loanId}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ content: newMessage }),
      });
      const data = await res.json();
      setMessages((prev) => [...prev, data]);
      setNewMessage('');
    } catch (err) {
      console.error('Error sending message:', err);
    }
  };

  const handleAcceptOffer = (offer) => {
    setSelectedOffer(offer);
    setShowContractModal(true);
  };

  // toggle a specific offer accordion
  const toggleOffer = (offerId) =>
    setExpandedOffer((prev) => ({ ...prev, [offerId]: !prev[offerId] }));

  if (!loan) return <p>Loading loan details...</p>;

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Loan Request by {loan.borrower?.name || 'Unknown User'}</h2>

      <div style={{ display: 'flex', gap: '12px', alignItems: 'flex-start', marginBottom: '1rem' }}>
        <UserProfileModal user={loan.borrower} />
      </div>

      <p><strong>Amount:</strong> ${loan.amount}</p>
      <p><strong>Duration:</strong> {loan.duration} months</p>
      <p><strong>Reason:</strong> {loan.purpose}</p>
      <p><strong>Status:</strong> {loan.status}</p>

      <hr />
      <h3>Offers</h3>

      {loan.loanOffers?.length > 0 ? (
        loan.loanOffers.map((offer) => {
          const isOpen = !!expandedOffer[offer.id];
          const statusLabel = loan.status === 'FUNDED' ? 'Accepted' : 'Pending';
          const badgeStyle =
            loan.status === 'FUNDED'
              ? { background: '#dcfce7', color: '#166534', borderColor: '#86efac' }
              : { background: '#fef3c7', color: '#92400e', borderColor: '#fde68a' };

          return (
            <div
              key={offer.id}
              style={{
                border: '1px solid #e2e8f0',
                borderRadius: 12,
                background: '#fff',
                marginBottom: 12,
                overflow: 'hidden',
              }}
            >
              {/* Header row (click to open/close) */}
              <button
                onClick={() => toggleOffer(offer.id)}
                aria-expanded={isOpen}
                style={{
                  width: '100%',
                  textAlign: 'left',
                  background: '#f8fafc',
                  border: 0,
                  padding: '12px 14px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  cursor: 'pointer',
                }}
              >
                <div style={{ display: 'flex', alignItems: 'center', gap: 10 }}>
                  <span
                    style={{
                      width: 8,
                      height: 8,
                      borderRadius: 999,
                      background: '#6366f1',
                      display: 'inline-block',
                    }}
                  />
                  <span style={{ fontWeight: 700, color: '#0f172a' }}>
                    Offer from {offer.lender?.name || 'Unknown'}
                  </span>
                </div>

                <div style={{ display: 'flex', gap: 12, alignItems: 'center', color: '#334155', fontSize: 14 }}>
                  <span>${Number(offer.amount || 0).toFixed(2)}</span>
                  <span>{offer.interestRate}%</span>
                  <span>{offer.duration} mo</span>
                  <span
                    style={{
                      borderRadius: 999,
                      padding: '2px 8px',
                      fontSize: 12,
                      fontWeight: 700,
                      border: '1px solid',
                      ...badgeStyle,
                    }}
                  >
                    {statusLabel}
                  </span>
                  <span style={{ fontSize: 18, color: '#64748b' }}>{isOpen ? 'â–¾' : 'â–¸'}</span>
                </div>
              </button>

              {/* Body */}
              {isOpen && (
                <div style={{ padding: '12px 14px 14px', borderTop: '1px solid #e2e8f0' }}>
                  <p><strong>Message:</strong> {offer.message || 'â€”'}</p>

                  {loan.status === 'OPEN' && userId === loan.borrower?.id && (
                    <button
                      onClick={() => handleAcceptOffer(offer)}
                      style={{
                        background: '#4f46e5',
                        border: '1px solid #4f46e5',
                        color: '#fff',
                        fontWeight: 700,
                        padding: '8px 12px',
                        borderRadius: 10,
                        cursor: 'pointer',
                      }}
                    >
                      Accept Offer
                    </button>
                  )}
                </div>
              )}
            </div>
          );
        })
      ) : (
        <p>No offers yet.</p>
      )}

      {/* Submit an Offer (Amount is locked to loan.amount) */}
      {loan.status === 'OPEN' && loan.borrower?.id !== userId && (
        <div style={{ marginTop: '2rem', padding: '1rem', border: '1px solid #e2e8f0', borderRadius: 10 }}>
          <h4 style={{ marginTop: 0 }}>Submit an Offer</h4>

          {/* Fixed Amount */}
          <div style={{ marginBottom: 12 }}>
            <label style={{ display: 'block', fontWeight: 700, marginBottom: 6 }}>Amount ($)</label>
            <div
              title="Amount is fixed to the requested amount"
              style={{
                height: 38,
                display: 'flex',
                alignItems: 'center',
                padding: '0 12px',
                border: '1px solid #e5e7eb',
                borderRadius: 8,
                background: '#f8fafc',
                color: '#111827',
                fontWeight: 600,
              }}
            >
              ${Number(loan.amount || 0).toFixed(2)}
            </div>
          </div>

          {/* Editable controls */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
            <div>
              <label style={{ display: 'block', fontWeight: 700, marginBottom: 6 }}>Duration (months)</label>
              <input
                type="number"
                min="1"
                name="duration"
                value={offerForm.duration}
                onChange={(e) => setOfferForm((p) => ({ ...p, duration: Number(e.target.value) }))}
                style={{ width: '100%', padding: '8px 10px', border: '1px solid #e5e7eb', borderRadius: 8 }}
              />
            </div>

            <div>
              <label style={{ display: 'block', fontWeight: 700, marginBottom: 6 }}>Interest Rate (APR %)</label>
              <input
                type="number"
                min="0"
                step="0.01"
                name="interestRate"
                value={offerForm.interestRate}
                onChange={(e) => setOfferForm((p) => ({ ...p, interestRate: Number(e.target.value) }))}
                style={{ width: '100%', padding: '8px 10px', border: '1px solid #e5e7eb', borderRadius: 8 }}
              />
            </div>
          </div>

          <div style={{ marginTop: 12 }}>
            <label style={{ display: 'block', fontWeight: 700, marginBottom: 6 }}>Message (optional)</label>
            <textarea
              name="message"
              value={offerForm.message}
              onChange={(e) => setOfferForm((p) => ({ ...p, message: e.target.value }))}
              rows={3}
              style={{ width: '100%', padding: '8px 10px', border: '1px solid #e5e7eb', borderRadius: 8 }}
            />
          </div>

          <div style={{ marginTop: 12 }}>
            <button
              onClick={submitOffer}
              style={{
                background: '#4f46e5',
                border: '1px solid #4f46e5',
                color: '#fff',
                fontWeight: 700,
                padding: '8px 12px',
                borderRadius: 10,
                cursor: 'pointer',
              }}
            >
              Submit Offer
            </button>
          </div>
        </div>
      )}

      {loan.status === 'FUNDED' && userId === loan.borrower?.id && (
        <div style={{ marginTop: '2rem' }}>
          <h3>Repayments</h3>
          <button onClick={() => setShowRepaymentForm(true)}>Make a Payment</button>
          <button onClick={() => navigate(`/loan/${loan.id}/repayments`)}>View Repayment Tracker</button>
          {showRepaymentForm && <RepaymentForm loanId={loan.id} />}
        </div>
      )}

      <hr />

      {/* Discussion */}
      <div id="discussion-thread" style={{ marginTop: '1.5rem' }}>
        <h3>Public Discussion</h3>
        {messages.length === 0 ? (
          <p>No messages yet. Start the conversation!</p>
        ) : (
          messages.map((m) => (
            <div key={m.id} style={{ borderTop: '1px solid #e5e7eb', padding: '8px 0' }}>
              <strong>{m.user?.name || 'User'}:</strong> {m.content}
            </div>
          ))
        )}
        <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
          <textarea
            placeholder="Write your messageâ€¦"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            style={{ flex: 1, padding: 8, border: '1px solid #e5e7eb', borderRadius: 8 }}
          />
          <button
            onClick={handleSendMessage}
            style={{
              background: '#4f46e5',
              border: '1px solid #4f46e5',
              color: '#fff',
              fontWeight: 700,
              padding: '8px 12px',
              borderRadius: 10,
              cursor: 'pointer',
              height: 40,
              alignSelf: 'center',
            }}
          >
            Send
          </button>
        </div>
      </div>

      {showContractModal && selectedOffer && (
        <ContractModal
          offer={selectedOffer}
          borrower={loan.borrower}
          onConfirm={async () => {
            try {
              const res = await fetch(`/api/loans/offers/${selectedOffer.id}/accept`, {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                  Authorization: `Bearer ${token}`,
                },
              });

              if (!res.ok) {
                const errorText = await res.text();
                alert(`âŒ ${errorText || 'Something went wrong'}`);
                return;
              }

              await res.json();
              alert('âœ… Loan contract created and saved!');
              setShowContractModal(false);
              navigate('/dashboard');
            } catch (err) {
              console.error('âŒ Error finalizing contract:', err);
              alert('Network or server error occurred.');
            }
          }}
          onCancel={() => setShowContractModal(false)}
        />
      )}
    </div>
  );
};

export default LoanDetails;
