// src/features/wallet/WalletPanel.jsx
import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { createDepositIntent, fetchWallet } from './walletApi';

const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLISHABLE_KEY);

export default function WalletPanel({ onClose, onBalanceUpdated }) {
  const [amount, setAmount] = useState('25.00');
  const [busy, setBusy] = useState(false);

  const numericDollars =
    Number((amount || '').replace(/[^0-9.]/g, '')) || 0;

  const deposit = async () => {
    try {
      setBusy(true);

      if (!numericDollars || numericDollars <= 0) {
        alert('Please enter an amount greater than 0.');
        return;
      }

      const { clientSecret, simulated } = await createDepositIntent({
        amountDollars: numericDollars,
      });

      if (!simulated) {
        const stripe = await stripePromise;
        const result = await stripe.confirmPayment({
          elements: null,
          clientSecret,
        }).catch(async () => {
          // older stripe.js versions:
          return stripe.confirmCardPayment(clientSecret);
        });

        if (result.error) throw new Error(result.error.message);
      }

      const w = await fetchWallet();
      onBalanceUpdated?.(w.availableCents);
      onClose?.();
    } catch (e) {
      alert(e.message || 'Deposit failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="wallet-panel">
      <h3>Add funds</h3>
      <div className="row">
        <label>Amount</label>
        <div className="input-wrap">
          <span>$</span>
          <input
            value={amount}
            onChange={e => setAmount(e.target.value)}
            inputMode="decimal"
            placeholder="25.00"
          />
        </div>
      </div>
      <div className="row right">
        <button className="btn" onClick={onClose}>Cancel</button>
        <button className="btn primary" onClick={deposit} disabled={busy}>
          Continue
        </button>
      </div>
    </div>
  );
}
