// src/features/dashboard/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import TopLenders from '../../components/TopLenders';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState('');
  const [userName, setUserName] = useState('User');
  const [superUser, setSuperUser] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const init = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const res = await fetch('/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.ok) throw new Error('Failed to fetch user');

        const data = await res.json();
        setUserName(data.name || 'User');
        setSuperUser(data.isSuperUser || false);
      } catch (err) {
        console.error('Failed to fetch user profile:', err);
        navigate('/login');
      } finally {
        setLoading(false);
      }

      fetchPosts();
    };

    init();
  }, []); // keeping your original behavior

  const validateUserSession = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);
      setUserName(decoded.name || 'User');
      setSuperUser(decoded.superUser || false);
      setLoading(false);
    } catch (err) {
      console.error('Error decoding token:', err);
      navigate('/login');
    }
  };

  const fetchPosts = async () => {
    try {
      const res = await fetch('/api/posts', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      if (!res.ok) throw new Error('Failed to load posts');
      const data = await res.json();
      setPosts(data);
    } catch (err) {
      console.error('Error fetching posts:', err);
    }
  };

  const handlePost = async () => {
    if (!newPost.trim()) return;

    try {
      const res = await fetch('/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ content: newPost }),
      });

      if (!res.ok) throw new Error('Failed to create post');
      setNewPost('');
      fetchPosts();
    } catch (err) {
      console.error('Error posting:', err);
      alert('Failed to post.');
    }
  };

  const handleUpgrade = async () => {
    try {
      const token = localStorage.getItem('token');

      const res = await fetch('/api/users/create-superuser-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });

      const data = await res.json();
      window.location.href = data.url;
    } catch (err) {
      console.error('Upgrade error:', err);
    }
  };

  const getToday = () => {
    const days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    const today = new Date();
    return days[today.getDay()];
  };

  if (loading) {
    return (
      <div className="dashboard-container center">
        <div className="loading">Loading your dashboardâ€¦</div>
      </div>
    );
  }

return (
  <div className="db-container">
    <div className="db-header">
      <h2 className="db-title">Hey {userName}, Happy {getToday()}!</h2>

      {!superUser && (
        <div className="db-cta">
          <div className="db-cta-text">
            <h3>ðŸ’Ž Become a Superuser</h3>
            <p>Skip the 3% platform fee and support PeerFund for just <strong>$1/mo</strong>.</p>
          </div>
          <button className="action-btn primary" onClick={handleUpgrade}>
            Upgrade Now
          </button>
        </div>
      )}
    </div>

    {/* 2-column content grid */}
    <div className="db-grid">
      {/* Left column */}
      <section className="db-card">
        <h3 className="db-card-title">Share with the community</h3>
        <div className="db-composer">
          <input
            type="text"
            placeholder="Whatâ€™s on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <button className="action-btn" onClick={handlePost}>Post</button>
        </div>

        <h4 className="db-subtitle">ðŸ“¢ PeerFeed</h4>
        <div className="db-feed">
          {posts.length === 0 ? (
            <div className="db-empty">No posts yet. Be the first to say hi!</div>
          ) : (
            posts.map((post, index) => (
              <article key={index} className="db-feed-item">
                <div className="db-feed-top">
                  <span className="db-feed-author">{post.user?.name || 'Unknown'}</span>
                  <time className="db-feed-time">
                    {new Date(post.createdAt).toLocaleString()}
                  </time>
                </div>
                <p className="db-feed-content">{post.content}</p>
              </article>
            ))
          )}
        </div>
      </section>

      {/* Right column */}
      <aside className="db-card">
        <div className="leaderboard-wrap">
          <TopLenders />
        </div>

      <div className="db-tip">
       <h1><button className="action-btn" onClick={() => navigate('/create-loan')}>Request Loan</button></h1>
       <h4>How requesting a loan works:<br></br>
       â€¢ $50 - Automaticically get funds w/ 10% Interest<br></br>
       â€¢ $75 - Automaticically get funds w/ 9% Interest<br></br>
       â€¢ $100 - Automaticically get funds w/ 8% Interest<br></br>
       â€¢ $250 - $1,000 - PeerFunded Loan accepted by another user</h4>
       </div>
      </aside>
    </div>
  </div>

  );
};

export default Dashboard;
